import React, {useState,useContext, useEffect,useRef} from 'react'
import client from '../../feathers'
import {DebounceInput} from 'react-debounce-input';
import { useForm } from "react-hook-form";
import {DocumentClassList} from './DocumentClass'
//import {useHistory} from 'react-router-dom'
import {UserContext,ObjectContext} from '../../context'
import {toast} from 'bulma-toast'

export default function PulmonologyIntake() {

    const { register, handleSubmit,setValue} = useForm(); //, watch, errors, reset 
    const [error, setError] =useState(false)
    const [success, setSuccess] =useState(false)
    const [message,setMessage] = useState("")
    // eslint-disable-next-line
    const [facility,setFacility] = useState()
    const ClientServ=client.service('clinicaldocument')
    //const history = useHistory()
    const {user} = useContext(UserContext) //,setUser
    // eslint-disable-next-line
    const [currentUser,setCurrentUser] = useState()
    const [allergy,setAllergy] = useState("")
    const [reaction,setReaction] = useState("")
    const [allergine,setAllergine] = useState("")
    const [allergies,setAllergies] = useState([])
    const [duration,setDuration] = useState("")
    const [symptom,setSymptom] = useState("")
    const [symptoms,setSymptoms] = useState([])
    const [docStatus,setDocStatus] = useState("Draft")

    const [dataset,setDataset] = useState()
    const {state}=useContext(ObjectContext)


     let draftDoc=state.DocumentClassModule.selectedDocumentClass.document


     //state.DocumentClassModule.selectedDocumentClass.name

     useEffect(() => {
         if(!!draftDoc && draftDoc.status==="Draft"){

            Object.entries(draftDoc.documentdetail).map(([keys,value],i)=>(
                setValue(keys, value,  {
                    shouldValidate: true,
                    shouldDirty: true
                })

            ))
            setSymptoms(draftDoc.documentdetail.Presenting_Complaints)
            setAllergies(draftDoc.documentdetail.Allergy_Skin_Test)
    }
         return () => {
             draftDoc={}
         }
     }, [draftDoc])

    const getSearchfacility=(obj)=>{
        setValue("facility", obj._id,  {
            shouldValidate: true,
            shouldDirty: true
        })
    }

    useEffect(() => {
        setCurrentUser(user)
        //console.log(currentUser)
        return () => {

        }
    }, [user])

  //check user for facility or get list of facility  
    useEffect(()=>{
        //setFacility(user.activeClient.FacilityId)//
      if (!user.stacker){
       /*    console.log(currentUser)
        setValue("facility", user.currentEmployee.facilityDetail._id,  {
            shouldValidate: true,
            shouldDirty: true
        })  */
      }
    })

    const coughinfo =["productive", "dry", "barking", "paroxysimal", "post-tusive vomiting ", "worse at night ", 
                        "worse at any time of the day ", "worse in certain posture " ,"progressive" ]
    const coughsymptoms=["fever", "catarrh", "night sweats", "weight loss", "contact with someone with chronic cough", "facial swelling", "leg swelling"]
    const coughsputum=["creamy", "brown", "blood stained", "whitish"]
    const resp=["Difficulty breathing","fast breathing","excessive sneezing", "allergy salute", "chest pain", "atopy", "family history of atopy"]
    const cvs=["cough","easy defatigability","breathelessness", "breathelessness  at rest", "breathelessness on exertion","Othopnea","Paroxymal nocturnal orthopnea","palpitation","chest pain"]
    const yesno=["Yes","No"]
    const urinary=["frequency","nocturia","polyuria","poor stream","incomplete bladder empty","urgency","hesistancy"]
    const neuro=["headache","neck pain","neck stiffness","vertigo","dizziness","fainting spells","akward gait","weakness of upper limbs","weakness of lower limbs"]
    const headache=["throbing", "dull", "generalised", "frontal", "right-sided", "left sided", "photophia"]
    const limbs=["Right Limb", "Left Limb", "Both Limbs"]
    const side=["Right","Left","Both"]
    const eardis=["Right","Left","Both", "Purulent","bloody"]
    const ent=["Sore throat", "change in voice","nasal discharge","excessive sneezing","allergy salute"]
    const endo=["heat intolerance", "apathy", "excessive sweating","excessive hair growth","weight gain", "weight loss", "menstral irregularity"]
    const birthmode = ["Spontenous varginal delivery","Elective Suregery", "Emergency Surgery"]
    const vachist=["caregiver's report", "child health card"]
    const pernotes=["dull", "resonant", "hyper-resonant"]
    const pulsenature=["Regular","Irregular", "Normal volume", "Pounding", "Synchronous", "Asynchronous"	]
    const heartsound=["S1","S2","S3","S4"]
    const abd=["Full","Distended", "Flat","moves with respiration","Abdominal vein visible"]
    const bowelsound=["Normal", "absent", "hyperactive", "reduced or hypoactive"]


       /*  const joins=(p)=>{
        let x=p.split(" ")
        console.log(x)
        x.forEach((el,i)=>({
            setSub(prev => (prev+"_"+el))
        }
        ))
    } */
    const onSubmit = (data,e) =>{
        e.preventDefault();
        setMessage("")
        setError(false)
        setSuccess(false)
        let document={}
         // data.createdby=user._id
         // console.log(data)
          data.Presenting_Complaints=symptoms
          data.Allergy_Skin_Test=allergies

          if (user.currentEmployee){
          document.facility=user.currentEmployee.facilityDetail._id 
          document.facilityname=user.currentEmployee.facilityDetail.facilityName // or from facility dropdown
          }
         document.documentdetail=data
          document.documentname="Pediatric Pulmonology Form"  //"Lab Result"
         // document.documentClassId=state.DocumentClassModule.selectedDocumentClass._id
          document.location=state.employeeLocation.locationName +" "+ state.employeeLocation.locationType
          document.locationId=state.employeeLocation.locationId
          document.client=state.ClientModule.selectedClient._id
          document.createdBy=user._id
          document.createdByname=user.firstname+ " "+user.lastname
          document.status=docStatus==="Draft"?"Draft":"completed"
          //console.log(document)

          if (
            document.location===undefined ||!document.createdByname || !document.facilityname ){
            toast({
                message: ' Documentation data missing, requires location and facility details' ,
                type: 'is-danger',
                dismissible: true,
                pauseOnHover: true,
              })
              return
          }
        let confirm = window.confirm(`You are about to save this document ${ document.documentname} ?`)
        if (confirm){

        if (!!draftDoc &&  draftDoc.status==="Draft"){
            ClientServ.patch(draftDoc._id, document)
            .then((res)=>{
                    //console.log(JSON.stringify(res))
                    e.target.reset();
                    setAllergies([])
                    setSymptoms([])
                   /*  setMessage("Created Client successfully") */
                    setSuccess(true)
                    toast({
                        message: 'Pediatric Pulmonology Form updated succesfully',
                        type: 'is-success',
                        dismissible: true,
                        pauseOnHover: true,
                      })
                      setSuccess(false)
                })
                .catch((err)=>{
                    toast({
                        message: 'Error updating Pediatric Pulmonology Form ' + err,
                        type: 'is-danger',
                        dismissible: true,
                        pauseOnHover: true,
                      })
                })

        }else{
        ClientServ.create(document)
        .then((res)=>{
                //console.log(JSON.stringify(res))
                e.target.reset();
                setAllergies([])
                setSymptoms([])
               /*  setMessage("Created Client successfully") */
                setSuccess(true)
                toast({
                    message: 'Pediatric Pulmonology Form created succesfully',
                    type: 'is-success',
                    dismissible: true,
                    pauseOnHover: true,
                  })
                  setSuccess(false)
            })
            .catch((err)=>{
                toast({
                    message: 'Error creating Pediatric Pulmonology Form ' + err,
                    type: 'is-danger',
                    dismissible: true,
                    pauseOnHover: true,
                  })
            })
        }
        }
      } 



        const handleChangePart=async (e)=>{
            //console.log(e)
            //const (name, value) = e.target
            let {name, value}= e.target
            console.log(name,value)
        await   setDataset((prev ) => ({...prev, [name]:value}))
        //  console.log(dataset)

        }
        const handleChangeStatus=async (e)=>{
        // await setAppointment_type(e.target.value)

        setDocStatus(e.target.value)

        //console.log(e.target.value)

        }

      /*   useEffect(() => {

            return () => {

            }
        }, [docStatus]) */

        const handleAllergy=async (e)=>{
            //console.log(e)
            //const (name, value) = e.target
            const {name, value}= e.target
            console.log(name,value)
           // [name]=value
         await   setAllergy((prev ) => ({...prev, [name]:value}))
          console.log(allergy)

        }


    const handleAdd = ()=>{
        let allergy = {
            allergine:allergine,
            reaction:reaction
        } 
        setAllergies((prev)=>([...prev, allergy]))
        setAllergy({})
        setReaction("")
        setAllergine("")

    }
    const handleAddSymptoms = ()=>{
        let newsymptom = {
            symptom,
            duration
        } 
        setSymptoms((prev)=>([...prev, newsymptom]))
       // setAllergy({})
        setSymptom("")
        setDuration("")

    }

    return (
        <>
        <div className="card ">
            <div className="card-header">
                <p className="card-header-title">
                Pediatric Pulmonology Form
                </p>
            </div>
            <div className="card-content vscrollable remPad1">

              {/*   <label className="label is-size-7">
                  Client:  {order.orderInfo.orderObj.clientname}
                </label>
                <label className="label is-size-7">
                 Test:  {order.serviceInfo.name}
                </label> */}
            <form onSubmit={handleSubmit(onSubmit)}>
                <div className="field">
                        <p className="control ">
                            <input className="input is-small"   ref={register} name="Name" type="text" placeholder="Name"  />

                        </p>
                </div>
                <div className="field">
                        <p className="control ">
                            <input className="input is-small"   ref={register} name="Age" type="text" placeholder="Age" />          
                        </p>
                </div>

                <div className="field">
                <label>Gender</label> 
                                <label className=" is-small ml-2">
                                        <input  type="radio" ref={register} name="Gender"  value="Male"   onChange={(e)=>{handleChangePart(e)}}/>
                                            <span > Male</span>
                                </label> 
                                <label className=" is-small ml-2">
                                    <input type="radio" ref={register} name="Gender"   value="Female"  onChange={(e)=>handleChangePart(e)}/>
                                    <span>Female</span>
                                </label>
                </div>
                <div className="field">
                        <p className="control ">
                            <input className="input is-small"   ref={register} name="Occupation" type="text" placeholder="Occupation" />

                        </p>
                </div>

               {/*  <div className="field">
                    <label >5. Race</label> 
                    <label className=" is-small ml-2">
                            <input  type="radio" ref={register} name="Race"  value="African"  onChange={(e)=>{handleChangePart(e)}}/>
                                <span > African</span>
                    </label> 
                    <label className=" is-small ml-2">
                        <input type="radio" ref={register} name="Race"   value="Caucasian"  onChange={(e)=>handleChangePart(e)}/>
                        <span>Caucasian</span>
                    </label>
                    <label className=" is-small ml-2">
                        <input type="radio" ref={register} name="Race"   value="Indian"  onChange={(e)=>handleChangePart(e)}/>
                        <span>Indian </span>
                    </label>
                    <label className=" is-small ml-2">
                        <input type="radio" ref={register} name="Race"   value="Others"   onChange={(e)=>handleChangePart(e)}/>
                        <span>Others </span>
                    </label>
                    <p className="control ">
                            <input className="input is-small"   ref={register} name="Others_race" type="text" placeholder="Highest Level of Education" />           
                        </p>
                </div> */}
                <div className="field">
                        <p className="control ">
                            <input className="input is-small"   ref={register} name="Marital_Status" type="text" placeholder="Marital Status" />

                        </p>
                </div>

                <div className="field">
                        <p className="control ">
                            <input className="input is-small"   ref={register} name="Address" type="text" placeholder="Address" />

                        </p>
                </div>
                <div className="field">
                        <p className="control ">
                            <input className="input is-small"   ref={register} name="Tribe" type="text" placeholder="Tribe" />

                        </p>
                </div>
                <div className="field">
                        <p className="control ">
                            <input className="input is-small"   ref={register} name="Informants" type="text" placeholder="Informants" />

                        </p>
                </div>
                <div className="field">
                        <p className="control ">
                            <input className="input is-small"   ref={register} name="Religion" type="text" placeholder="Religion" />

                        </p>
                </div>
                <div className="field">
                        <p className="control ">
                            <input className="input is-small"   ref={register} name="Father_Phone_Number" type="text" placeholder="Father's Phone Number" />

                        </p>
                </div>
                <div className="field">
                        <p className="control ">
                            <input className="input is-small"   ref={register} name="Mother_Phone_Number" type="text" placeholder="Mother's Phone Number" />

                        </p>
                </div>
                <div className="field">
                        <p className="control ">
                            <input className="input is-small"   ref={register} name="Next_of_Kin" type="text" placeholder="Next of Kin" />

                        </p>
                </div>

               {/*  <div className="field ">

                        <label >8. Highest Level of Education</label> 
                                <label className=" is-small ml-2">
                                        <input  type="radio" ref={register} name="Education" value="Uneducated" onChange={(e)=>{handleChangePart(e)}}/>
                                            <span > Uneducated</span>
                                </label> 
                                <label className=" is-small ml-2">
                                    <input type="radio" ref={register} name="Education"  value="Primary School" onChange={(e)=>handleChangePart(e)}/>
                                    <span>Primary School</span>
                                </label>
                                <label className=" is-small ml-2">
                                    <input type="radio" ref={register} name="Education"  value="Secondary School" onChange={(e)=>handleChangePart(e)}/>
                                    <span>Secondary School </span>
                                </label>
                                <label className=" is-small ml-2">
                                    <input type="radio" ref={register} name="Education"  value="Post-Secondary School (Diploma /Degree)" onChange={(e)=>handleChangePart(e)}/>
                                    <span>Post-Secondary School (Diploma /Degree) </span>
                                </label>
                                <label className=" is-small ml-2">
                                    <input type="radio" ref={register} name="Education"  value="Others" onChange={(e)=>handleChangePart(e)}/>
                                    <span>Others (Specify) </span>
                                </label>
                        <p className="control ">
                            <input className="input is-small"   ref={register} name="others_education" type="text" placeholder="Highest Level of Education" />           
                        </p>
                </div> */}
                <h3><b>Presentation Complaints</b></h3>
                <input className="input is-small is-hidden"   ref={register} name="Presenting_Complaints" type="text" placeholder="Specify" />  
                    <div className="field is-horizontal">
                        <div className="field-body ml-3">  
                            <div className="field">
                                <label className="is-small"> Symptom</label>
                            </div>
                            <div className="field">
                                <p className="control ">
                                    <input className="input is-small"  value={symptom} /* ref={register} */ onChange={(e)=>{setSymptom(e.target.value)}} name="symptom" type="text" placeholder="Symptom" />           
                                </p>
                            </div>

                        </div>
                    </div>
                    <div className="field is-horizontal">
                        <div className="field-body ml-3">  
                            <div className="field">
                                <label className="is-small">Duration</label>
                            </div>
                            <div className="field">
                                <p className="control ">
                                    <input className="input is-small"  value={duration}  /* ref={register} */ onChange={(e)=>{setDuration(e.target.value)}}  name="durationn" type="text" placeholder="Duration" />           
                                </p>
                            </div>
                        </div>
                        <div className="field">
                        <div className="control">
                            <div  className="button is-success is-small selectadd" onClick={handleAddSymptoms}>
                               Add
                            </div>
                         </div>
                        </div>
                    </div>
                    <table className="table is-striped  is-hoverable is-fullwidth is-scrollable mr-2">
                        <thead>
                            <tr>
                            <th><abbr title="Serial No">S/No</abbr></th>

                            <th><abbr title="Type"> Symptom</abbr></th>
                            <th><abbr title="Destination">Duration</abbr></th>
                            </tr>
                        </thead>
                        <tfoot>

                        </tfoot>
                        <tbody>
                        { symptoms.map((ProductEntry, i)=>(

                                <tr key={i}>
                                <th>{i+1}</th>
                                <td>{ProductEntry.symptom}</td> 
                                <td>{ProductEntry.duration}</td>                                                                     
                                </tr>

                            ))}
                        </tbody>
                        </table>
                <h3><b>Review of Systems</b></h3>

                <h4>i. Respiratory</h4>
                <div className="field is-horizontal">
                        <div className="field-body ml-3">  
                            <div className="field">
                                <label className="is-small"> (a) Cough</label>
                            </div>
                            <div className="field">
                                <label className=" is-small">
                                    <input  type="radio" ref={register} name="Cough" value="Yes" onChange={(e)=>{handleChangePart(e)}}/>
                                            <span > Yes</span>
                                </label> 
                                <label className=" is-small ml-2">
                                    <input type="radio" ref={register} name="Cough"  value="No" onChange={(e)=>handleChangePart(e)}/>
                                    <span>No </span>
                                </label>
                            </div>
                        </div>
                    </div>
                    <div className="field ml-3 ">
                    <label className= "mr-2 "> <b>Cough nature:</b></label>
