/* eslint-disable */
import React, {useState,useContext, useEffect} from 'react'
import client from '../../feathers'
import {DebounceInput} from 'react-debounce-input';
import { useForm } from "react-hook-form";
import {toast} from 'bulma-toast'
//import {useHistory} from 'react-router-dom'
import {UserContext,ObjectContext} from '../../context'
import {FacilitySearch} from '../helpers/FacilitySearch'


export default function OrganizationClient() {
    const {state}=useContext(ObjectContext) //,setState
    // eslint-disable-next-line
    const [selectedFacility,setSelectedFacility]=useState()
    const [success, setSuccess] =useState(false)

    //const [showState,setShowState]=useState() //create|modify|detail

    //console.log("Organization parent", state)


    return(
        <section className= "section remPadTop">
           {/*  <div className="level">
            <div className="level-item"> <span className="is-size-6 has-text-weight-medium">Organization  Module</span></div>
            </div> */}
            <div className="columns ">
            <div className="column is-8 ">
                <OrganizationList />
                </div>
            <div className="column is-4 ">
                {/* {(state.facilityModule.show ==='create')&&<OrganizationCreate />} */}
                {(state.facilityModule.show ==='detail')&&<OrganizationDetail  />}
                {(state.facilityModule.show ==='modify')&&<OrganizationModify facility={selectedFacility} />}

            </div>

            </div>                            
            </section>

    )

}

export function OrganizationCreate(){
    const { register, handleSubmit} = useForm(); //, watch, errors, reset 
    const [error, setError] =useState(false)
    const [success, setSuccess] =useState(false)
    const [message,setMessage] = useState("")
    const facilityServ=client.service('facility')
    const orgServ=client.service('organizationclient')
    const [chosen, setChosen] =useState("")
    const [band, setBand]=useState("")
    const BandsServ=client.service('bands')
    const [providerBand,setProviderBand] = useState([])
    //const history = useHistory()
    const {user} = useContext(UserContext) //,setUser


   const handleChangeMode = async(e) => {
        await setBand(e.target.value)
      };
    /* const onSubmit = (data,e) =>{
        e.preventDefault();
        setMessage("")
        setError(false)
        setSuccess(false)
          data.createdby=user._id
          //console.log(data);

        facilityServ.create(data)
        .then((res)=>{
                //console.log(JSON.stringify(res))
                e.target.reset();
                setMessage("Created Organization successfully")
                setSuccess(true)
            })
            .catch((err)=>{
                setMessage("Error creating facility, probable network issues "+ err )
                setError(true)
            })

      }  */
      const getProviderBand = async()=>{
        if (user.currentEmployee){

            const findServices= await BandsServ.find(
                    {query: {
                        facility: user.currentEmployee.facilityDetail._id,
                        bandType:(user.currentEmployee.facilityDetail.facilityType==="HMO")?"Provider":"Company",

                       // storeId:state.StoreModule.selectedStore._id,
                       // $limit:20,
                    //   paginate:false,
                        $sort: {
                            category: 1
                        }
                        }})
                       // console.log(findServices)
             await setProviderBand(findServices.data)
            // console.log(findServices)
                    }

    }

      const handleClick=()=>{
          //check band selected
          if (band===""){
            toast({
                message: 'Band not selected, Please select band',
                type: 'is-danger',
                dismissible: true,
                pauseOnHover: true,
            })
            return 
          }

        console.log(chosen)
        let stuff={
            facility:user.currentEmployee.facilityDetail._id ,
            organization:chosen._id,
            relationshiptype:"managedcare",
            band
        }
        orgServ.create(stuff)
        .then((res)=>{
                //console.log(JSON.stringify(res))
               // e.target.reset();
               setSuccess(true)
                toast({
                    message: 'Organization added succesfully',
                    type: 'is-success',
                    dismissible: true,
                    pauseOnHover: true,
                  })
                  setSuccess(false)
                  setBand("")
            })
            .catch((err)=>{
                toast({
                    message: 'Error adding organization ' + err,
                    type: 'is-danger',
                    dismissible: true,
                    pauseOnHover: true,
                  })
            })

      }

      useEffect(() => {
        // console.log("starting...")
          getProviderBand()
         return () => {

         }
        }, [])
      const getSearchfacility=(obj)=>{
          setChosen(obj)

       /*  setCategoryName(obj.categoryname)
        setChosen2(obj) */

         if (!obj){
             //"clear stuff"
            /*  setCategoryName("")
             setChosen2() */

         }
     }

    return (
        <>

          <div className="field is-horizontal">
             <div className="field-body">
                <div className="field is-expanded"  /* style={ !user.stacker?{display:"none"}:{}} */ >
                        <FacilitySearch  getSearchfacility={getSearchfacility} clear={success} /> 
                        <p className="control has-icons-left " style={{display:"none"}}>
                            <input className="input is-small" /* ref={register ({ required: true }) }  *//* add array no */  /* value={facilityId} name="facilityId" type="text" onChange={e=>setFacilityId(e.target.value)} placeholder="Product Id" */ />
                            <span className="icon is-small is-left">
                            <i className="fas  fa-map-marker-alt"></i>
                            </span>
                        </p>
                    </div>
                    <div className="field">    
                        <div className="control">
                            <div className="select is-small ">
                                <select name="bandType" value={band} onChange={(e)=>handleChangeMode(e)} className="selectadd" >
                                <option value="">{(user.currentEmployee.facilityDetail.facilityType==="HMO")?"Choose Provider Band":"Choose Company Band"} </option>
                                {providerBand.map((option,i)=>(
                                    <option key={i} value={option.name}> {option.name}</option>
                                ))}
                                </select>
                            </div>
                        </div>
                    </div>
                    <div className="field">
                        <p className="control">
                                <button className="button is-success is-small selectadd">
                                <span className="is-small" onClick={handleClick}>Add</span>
                                </button>
                            </p>
                        </div>
                    </div>
                    </div>

         </>
    )

}

export function OrganizationList(){
   // const { register, handleSubmit, watch, errors } = useForm();
    // eslint-disable-next-line
    const [error, setError] =useState(false)
     // eslint-disable-next-line
    const [success, setSuccess] =useState(false)
     // eslint-disable-next-line
   const [message, setMessage] = useState("") 
    const facilityServ=client.service('facility')
    const orgServ=client.service('organizationclient')
    //const history = useHistory()
   // const {user,setUser} = useContext(UserContext)
    const [facilities,setFacilities]=useState([])
     // eslint-disable-next-line
   const [selectedFacility, setSelectedFacility]=useState() //
    // eslint-disable-next-line
    const {state,setState}=useContext(ObjectContext)
    const {user} = useContext(UserContext)



    const handleCreateNew = async()=>{
        const    newfacilityModule={
            selectedFacility:{},
            show :'create'
            }
       await setState((prevstate)=>({...prevstate, facilityModule:newfacilityModule}))
       //console.log(state)


    }
    const handleRow= async(facility)=>{
        //console.log("b4",state)

        //console.log("handlerow",facility)

        await setSelectedFacility(facility.organizationDetail)

        const    newfacilityModule={
            selectedFacility:facility,
            show :'detail'
        }
       await setState((prevstate)=>({...prevstate, facilityModule:newfacilityModule}))
       //console.log(state)

    }

   const handleSearch=(val)=>{
       const field='facilityName'
       console.log(val)
       if (val.length >0){
       orgServ.find({query: {
                /* [field]: {
                    $regex:val,
                    $options:'i'

                }, */
                facility:user.currentEmployee.facilityDetail._id,
                $search:val,
                $limit:10,
                $sort: {
                    createdAt: -1
                  }
                    }}).then((res)=>{
                console.log(res)
               setFacilities(res.data)
                setMessage(" Organization  fetched successfully")
                setSuccess(true) 
            })
            .catch((err)=>{
                console.log(err)
                setMessage("Error creating facility, probable network issues "+ err )
                setError(true)
            })
        }else{
            getFacilities() 
        }
        }

           /*  if (val.length>2){
                console.log("in")

            }

        }
     */
        const getFacilities=()=>{
            orgServ.find({query: {
                facility:user.currentEmployee.facilityDetail._id,
                $limit:100,
                $sort: {
                    createdAt: -1
                  }
                    }})
            .then((res)=>{
                console.log(res)
                    setFacilities(res.data)
                    setMessage(" Organization  fetched successfully")
                    setSuccess(true)
                })
                .catch((err)=>{
                    setMessage("Error creating facility, probable network issues "+ err )
                    setError(true)
                })

        }

    useEffect(() => {
        getFacilities()

        orgServ.on('created', (obj)=>getFacilities())
        orgServ.on('updated', (obj)=>getFacilities())
        orgServ.on('patched', (obj)=>getFacilities())
        orgServ.on('removed', (obj)=>getFacilities())
        return () => {

        }
    },[])

    //todo: pagination and vertical scroll bar

    return(
            <>   <OrganizationCreate />
                <div className="level">
                    <div className="level-left">
