/* eslint-disable */
import React, {useState,useContext, useEffect,useRef} from 'react'
import {Route, Switch,  useRouteMatch, Link, NavLink, useHistory} from 'react-router-dom'
import client from '../../feathers'
import {DebounceInput} from 'react-debounce-input';
import { useForm } from "react-hook-form";
//import {useHistory} from 'react-router-dom'
import {UserContext,ObjectContext} from '../../context'
import {toast} from 'bulma-toast'
import { formatDistanceToNowStrict, format, subDays,addDays } from 'date-fns'
import DatePicker from "react-datepicker";
import LocationSearch from "../helpers/LocationSearch"
import EmployeeSearch from "../helpers/EmployeeSearch"
import BillServiceCreate from '../Finance/BillServiceCreate'
import "react-datepicker/dist/react-datepicker.css";
// eslint-disable-next-line
const searchfacility={};


export default function RadCheckedin() {
    const {state}=useContext(ObjectContext) //,setState
    // eslint-disable-next-line
    const [selectedClient,setSelectedClient]=useState()
    const [selectedAppointment,setSelectedAppointment]=useState()
    //const [showState,setShowState]=useState() //create|modify|detail

    return(
        <section className= "section remPadTop">
            <div className="columns ">

            <div className="column is-6 ">
                <RadStatusList />
                </div>
            <div className="column is-6 ">
            <RadCheckedOutList />
               {/*  {(state.AppointmentModule.show ==='create')&&<RadAppointmentCreate />}
                {(state.AppointmentModule.show ==='detail')&&<RadAppointmentDetail  />}
                {(state.AppointmentModule.show ==='modify')&&<RadAppointmentModify Client={selectedClient} />} */}

            </div>

            </div>                            
            </section>

    )

}

export function RadAppointmentCreate(){
    const {state,setState}=useContext(ObjectContext) 
    const { register, handleSubmit,setValue} = useForm(); //, watch, errors, reset 
    const [error, setError] =useState(false)
    const [success, setSuccess] =useState(false)
    const [success1, setSuccess1] =useState(false)
    const [success2, setSuccess2] =useState(false)
    const [message,setMessage] = useState("")
    const [clientId,setClientId] = useState()
    const [locationId,setLocationId] = useState()
    const [practionerId,setPractionerId] = useState()
    const [type,setType] = useState()
    // eslint-disable-next-line
    const [facility,setFacility] = useState()
    const ClientServ=client.service('appointments')
    //const history = useHistory()
    const {user} = useContext(UserContext) //,setUser
    // eslint-disable-next-line
    const [currentUser,setCurrentUser] = useState()
    const [selectedClient,setSelectedClient]=useState()
    const [selectedAppointment,setSelectedAppointment]=useState()
   // const [appointment_reason,setAppointment_reason]= useState()
    const [appointment_status,setAppointment_status]=useState("")
    const [appointment_type, setAppointment_type]=useState("")
    const [billingModal, setBillingModal] =useState(false)

    const [chosen, setChosen]=useState()
    const [chosen1, setChosen1]=useState()
    const [chosen2, setChosen2]=useState()
    const appClass=[ "On-site","Teleconsultation","Home Visit"]

    let appointee  //  =state.ClientModule.selectedClient 
   /*  const getSearchfacility=(obj)=>{
        setValue("facility", obj._id,  {
            shouldValidate: true,
            shouldDirty: true
        })
    } */
    const handleChangeType=async (e)=>{
        await setAppointment_type(e.target.value)
    }

    const handleChangeStatus=async (e)=>{
        await setAppointment_status(e.target.value)
    }


    const getSearchfacility=(obj)=>{

       setClientId(obj._id)
       setChosen(obj)
       //handleRow(obj)
        if (!obj){
            //"clear stuff"
            setClientId()
            setChosen()

        }


       /*  setValue("facility", obj._id,  {
            shouldValidate: true,
            shouldDirty: true
        }) */
    }
    const getSearchfacility1=(obj)=>{

        setLocationId(obj._id)
        setChosen1(obj)

         if (!obj){
             //"clear stuff"
             setLocationId()
             setChosen1()

         }
     }
     const getSearchfacility2=(obj)=>{

        setPractionerId(obj._id)
        setChosen2(obj)

         if (!obj){
             //"clear stuff"
             setPractionerId()
             setChosen2()

         }
     }

    useEffect(() => {
        setCurrentUser(user)
        //console.log(currentUser)
        return () => {

        }
    }, [user])

  //check user for facility or get list of facility  
    useEffect(()=>{
        //setFacility(user.activeClient.FacilityId)//
      if (!user.stacker){
       /*    console.log(currentUser)
        setValue("facility", user.currentEmployee.facilityDetail._id,  {
            shouldValidate: true,
            shouldDirty: true
        })  */
      }
    })

    const onSubmit = (data,e) =>{
        e.preventDefault();
        setMessage("")
        setError(false)
        setSuccess(false)
         // data.createdby=user._id
          console.log(data);
          if (user.currentEmployee){
          data.facility=user.currentEmployee.facilityDetail._id  // or from facility dropdown
          }
          data.locationId=locationId //state.ClinicModule.selectedClinic._id
          data.practitionerId=practionerId
          data.appointment_type=appointment_type
         // data.appointment_reason=appointment_reason
          data.appointment_status=appointment_status
          data.clientId=clientId
          data.firstname=chosen.firstname
            data.middlename=chosen.middlename
            data.lastname=chosen.lastname
            data.dob=chosen.dob
            data.gender=chosen.gender
            data.phone=chosen.phone
            data.email=chosen.email
            data.practitioner_name=chosen2.firstname+" "+chosen2.lastname
            data.practitioner_profession=chosen2.profession
            data.practitioner_department=chosen2.department
            data.location_name=chosen1.name
            data.location_type=chosen1.locationType
          data.actions=[{
              action:appointment_status,
              actor:user.currentEmployee._id
          }]
          console.log(data);


        ClientServ.create(data)
        .then((res)=>{
                //console.log(JSON.stringify(res))
                e.target.reset();
               setAppointment_type("")
               setAppointment_status("")
               setClientId("")
               setLocationId("")
               /*  setMessage("Created Client successfully") */
                setSuccess(true)
                setSuccess1(true)
                setSuccess2(true)
                toast({
                    message: 'Appointment created succesfully, Kindly bill patient if required',
                    type: 'is-success',
                    dismissible: true,
                    pauseOnHover: true,
                  })
                  setSuccess(false)
                  setSuccess1(false)
                  setSuccess2(false)
                 // showBilling()
            })
            .catch((err)=>{
                toast({
                    message: 'Error creating Appointment ' + err,
                    type: 'is-danger',
                    dismissible: true,
                    pauseOnHover: true,
                  })
            })

      } 

    useEffect(() => {
        getSearchfacility(state.ClientModule.selectedClient )

        /* appointee=state.ClientModule.selectedClient 
        console.log(appointee.firstname) */
        return () => {

        }
    }, [state.ClientModule.selectedClient ])

  /*   const showBilling = () =>{
        setBillingModal(true)
       //history.push('/app/finance/billservice')
        }
        const  handlecloseModal1 = () =>{
            setBillingModal(false)
            }


            const handleRow= async(Client)=>{
              //  await setSelectedClient(Client)
                const    newClientModule={
                    selectedClient:Client,
                    show :'detail'
                }
               await setState((prevstate)=>({...prevstate, ClientModule:newClientModule}))
            } */

    return (
        <>

            <div className="card ">
            <div className="card-header">
                <p className="card-header-title">
                    Create Appointment
                </p>
            </div>
            <div className="card-content vscrollable remPad1">

            <form onSubmit={handleSubmit(onSubmit)}>
            <div className="field is-horizontal">
            <div className="field-body">
           {/*  <label className="label is-small mr-2">Client:</label> */}

          {state.ClientModule.selectedClient.firstname !==undefined ? <> 
              <label className="label is-size-7" > {state.ClientModule.selectedClient.firstname} {state.ClientModule.selectedClient .lastname}</label>
               </> 
               : <div className="field is-expanded"  /* style={ !user.stacker?{display:"none"}:{}} */ >
                    <ClientSearch  getSearchfacility={getSearchfacility} clear={success} /> 
                    <p className="control has-icons-left " style={{display:"none"}}>
                        <input className="input is-small"  /* ref={register ({ required: true }) } */  /* add array no */  value={clientId} name="ClientId" type="text" onChange={e=>setClientId(e.target.value)} placeholder="Product Id" />
                        <span className="icon is-small is-left">
                        <i className="fas  fa-map-marker-alt"></i>
                        </span>
                    </p>
                 {/* {sellingprice &&   "N"}{sellingprice} {sellingprice &&   "per"}  {baseunit} {invquantity} {sellingprice &&   "remaining"}  */}
                </div>}
            </div>
        </div>
        <div className="field is-horizontal">
            <div className="field-body">
                <div className="field is-expanded"  /* style={ !user.stacker?{display:"none"}:{}} */ >
                    <LocationSearch  getSearchfacility={getSearchfacility1} clear={success1} /> 
                    <p className="control has-icons-left " style={{display:"none"}}>
                        <input className="input is-small"  /* ref={register ({ required: true }) } */  /* add array no */  value={locationId} name="locationId" type="text" onChange={e=>setLocationId(e.target.value)} placeholder="Product Id" />
                        <span className="icon is-small is-left">
                        <i className="fas  fa-map-marker-alt"></i>
                        </span>
                    </p>
                </div>
            </div>
        </div>
        <div className="field is-horizontal">
            <div className="field-body">
                <div className="field is-expanded"  /* style={ !user.stacker?{display:"none"}:{}} */ >
                    <EmployeeSearch  getSearchfacility={getSearchfacility2} clear={success2} /> 
