        const    newBandModule={
            selectedBand:Band,
            show :'modify'
        }
       await setState((prevstate)=>({...prevstate, BandModule:newBandModule}))
       //console.log(state)

    }

    return (
        <>
        <div className="card ">
            <div className="card-header">
                <p className="card-header-title">
                    Band Details
                </p>
            </div>
            <div className="card-content vscrollable">

                <table> 
                <tbody>         
                <tr>
                    <td>

