/* eslint-disable */
import React, {useState,useContext, useEffect,useRef} from 'react'
import client from '../../feathers'
import {DebounceInput} from 'react-debounce-input';
import { useForm } from "react-hook-form";
//import {useHistory} from 'react-router-dom'
import {UserContext,ObjectContext} from '../../context'
import {toast} from 'bulma-toast'
//import {ProductCreate} from './Products'
import Encounter from '../EncounterMgt/Encounter';
import  ServiceSearch  from '../helpers/ServiceSearch';
var random = require('random-string-generator');
// eslint-disable-next-line
const searchfacility={};

export default function AdmissionCreate(){
    // const { register, handleSubmit,setValue} = useForm(); //, watch, errors, reset 
     //const [error, setError] =useState(false)
     const [success, setSuccess] =useState(false)
     const [message,setMessage] = useState("")
     // eslint-disable-next-line
     const [facility,setFacility] = useState()
     const ProductEntryServ=client.service('productentry')
     const OrderServ=client.service('order')
     const LocationServ=client.service('location')
     const BillCreateServ=client.service('createbilldirect')
     const AdmissionServ=client.service('admission')
     //const history = useHistory()
     const {user} = useContext(UserContext) //,setUser
     // eslint-disable-next-line
     const [currentUser,setCurrentUser] = useState()
     const [type,setType] = useState("Bill")
     const [documentNo,setDocumentNo] = useState("")
     const [totalamount,setTotalamount] = useState(0)
     const [qamount,setQAmount] = useState(null)
     const [productId,setProductId] = useState("")
     const [source,setSource] = useState("")
     const [date,setDate] = useState("")
     const [name,setName] = useState("")
     const [inventoryId,setInventoryId] = useState("")
     const [baseunit,setBaseunit] = useState("")
     const [quantity,setQuantity] = useState(1)
     const [sellingprice,setSellingPrice] = useState("")
     const [costprice,setCostprice] = useState(0)
     const [invquantity,setInvQuantity] = useState("")
     const [calcamount,setCalcAmount] = useState(0)
     const [productItem,setProductItem] = useState([])
      const [billingId,setBilllingId]=useState("")  
      const [changeAmount, setChangeAmount] = useState(true)
      const [paymentmode, setPaymentMode] = useState("")
      const [paymentOptions, setPaymentOptions]=useState([])
      const [billMode, setBillMode]=useState("")
      const [productModal, setProductModal]=useState(false)
      const [obj, setObj]=useState("")
      const [objService, setObjService]=useState("")
      const [patient, setPatient]=useState("")
      const [contracts, setContracts]=useState("")
      const [category, setCategory]=useState("")
      const [chosenBed, setChosenBed]=useState()
      const [bedObject, setBedObject]=useState()

     const {state,setState}=useContext(ObjectContext)
     const inputEl = useRef(0);
     let calcamount1
     let hidestatus



  let medication =state.AdmissionModule.selectedAdmission
  let physicalbeds=state.WardModule.selectedWard?.sublocations
   //console.log(medication)

  const showDocumentation = async (value)=>{
    setProductModal(true)
  }
  const handlecloseModal =()=>{
    setProductModal(false)
   // handleSearch(val)
    }


    const handleChangeMode= async(value)=>{
       // console.log(value)
       await setPaymentMode(value)
       // console.log(paymentOptions)
       let billm= paymentOptions.filter(el=>el.name===value)
       await setBillMode(billm[0])
        //console.log(billm)
        // at startup
        // check payment mode options from patient financial info
        // load that to select options
        // default to HMO-->company-->family-->cash
        //when chosen
        //append payment mode to order
        //check service contract for pricing info
        // calculate pricing 
        // pricing


    }

    const handleRow= async(ProductEntry)=>{
    //console.log("b4",state)

    //console.log("handlerow",ProductEntry)

    //await setMedication(ProductEntry)

            const    newProductEntryModule={
                selectedMedication:ProductEntry,
                show :'detail'
            }
            await setState((prevstate)=>({...prevstate, medicationModule:newProductEntryModule}))
   //console.log(state)
  // ProductEntry.show=!ProductEntry.show

        } 

     const [productEntry,setProductEntry]=useState({
         productitems:[],
         date,
         documentNo,
         type,
         totalamount,
         source,

     })

     const productItemI={
         productId,
         name,
         quantity,
         sellingprice,
         amount:calcamount, //||qamount
         baseunit,
         costprice,
         category:"Prescription",  //category==="Inventory"?"Prescription":category,
         billingId,
         billMode

     }

     const checkPrice= async(contracts,billMode)=>{


        if( billMode.type==="HMO Cover"){ //paymentmode
         if (billMode.detail.plan==="NHIS"){
             //find contract for NHIS
             let contract=contracts.filter(el=>el.source_org_name==="NHIS")
             if (contract.length){
                // console.log(contract[0].price)
               await  setSellingPrice(contract[0].price)     
          }else{
             toast({
                 message: 'Please NHIS does not have cover/price for this service. Either set service price for NHIS, try another service or bill using cash',
                 type: 'is-danger',
                 dismissible: true,
                 pauseOnHover: true,
               })
              await setSellingPrice(0)
          }

         }else{

         let contract=contracts.filter(el=>el.source_org===billMode.detail.organizationId)
         if (contract.length){
           // console.log(contract[0].price)
            await setSellingPrice(contract[0].price)

          }else{
         toast({
             message: 'Please HMO does not have cover/price for this service. Either set service price for HMO , try another drug, bill using cash or adjust amount ',
             type: 'is-danger',
             dismissible: true,
             pauseOnHover: true,
           })
           await setSellingPrice(0)
      }

     }
     }
     if( billMode.type==="Company Cover"){ //paymentmode
         let contract=contracts.filter(el=>el.source_org===billMode.detail.organizationId)
         if (contract.length){
        // console.log(contract[0].price)
       await   setSellingPrice(contract[0].price)


        }else{

         toast({
             message: 'Please company does not have cover/price for this service. Either set service price for Company or try another drug or bill using cash',
             type: 'is-danger',
             dismissible: true,
             pauseOnHover: true,
           })
         await  setSellingPrice(0)   
      }

  }
  if( billMode.type==="Cash" || billMode.type==="Family Cover"){ //paymentmode
     let contract=contracts.filter(el=>el.source_org===el.dest_org)
     if (contract.length){
    // console.log(contract[0].price)
     await setSellingPrice(contract[0].price)


         }else{

     toast({
         message: 'Please there is no cover/price for this service. Either set service price or try another service. Setting price at zero ',
         type: 'is-danger',
         dismissible: true,
         pauseOnHover: true,
       })
       await setSellingPrice(0)   
  }

  }

    }
     // consider batchformat{batchno,expirydate,qtty,baseunit}
     //consider baseunoit conversions
     const getSearchfacility=async (service)=>{
        // console.log(obj)

        if (!service){
            //"clear stuff"
            setProductId("")
            setName("")
            setBaseunit("")
            setInventoryId("")
            setSellingPrice(0)
            setInvQuantity("")
            setQAmount(null)
            setCostprice("")
            setContracts("")
            setCategory("")
            setInventoryId("")
            setBilllingId("")
           // setCalcAmount(null)
            return
        }

        setContracts(service.contracts)
         setProductId(service.productId)
         setName(service.name)
         setCategory(service.category) //Lab Order
         setBaseunit(service.baseunit)
         setInventoryId(service.inventoryId)
         setBilllingId(service._id)
         await setObj(service)


    }
     useEffect(() => {

     }, [obj])

     useEffect(() => {
         setCurrentUser(user)
         //console.log(currentUser)
         return () => {

         }
     }, [user])

     const handleUpdateTotal=async ()=>{
        await setTotalamount(prevtotal=>Number(prevtotal) + Number(calcamount))
     }

     const handleChangeType=async (e)=>{
         //console.log(e.target.value)
         await setType(e.target.value)
     }

     const handleAmount= async()=>{
         await setQAmount(null)
        // alert("Iam chaning qamount")
     }

     const handleClickProd=async()=>{
       /*   console.log("amount: ",productItemI.amount)
         console.log("qamount: ",qamount)
         console.log("calcamount: ",calcamount) */
        if ( quantity===0||quantity===""|| productId===""||paymentmode==="" ){
            toast({
                message: 'You need to choose a product and quantity to proceed',
                type: 'is-danger',
                dismissible: true,
                pauseOnHover: true,
              }) 
              return 
        }

         await setSuccess(false)
         await setProductItem(
             prevProd=>prevProd.concat(productItemI)
         )
        handleUpdateTotal()
            // generate billing info
            const billInfo={
                orderInfo:{
                    orderId:medication._id,
                    orderObj:medication,
                  },
                  serviceInfo:{            
                    price: productItemI.sellingprice,
                    quantity: productItemI.quantity,
                    productId: productItemI.productId,
                    name: productItemI.name,
                    baseunit: productItemI.baseunit,
                    amount:productItemI.amount,
                    billingId:productItemI.billingId,
                    createdby:user._id,
                  },
                  paymentInfo:{
                    amountDue:productItemI.amount,
                    paidup:0,
                    balance:productItemI.amount,
                    paymentDetails:[]

                  },
                  participantInfo:{
                    billingFacility:medication.destination,
                    billingFacilityName:medication.destination_name,
                    locationId:state.StoreModule.selectedStore._id, //selected location,
                    clientId:medication.clientId,
                    client:medication.client,
                    paymentmode:billMode
                  },
                  createdBy:user.id,
                  billing_status:"Unpaid"
                }

        //update order

        OrderServ.patch(medication._id,{
            order_status:"Billed", //Billed
            billInfo,
        }).then((resp)=>{
           // medication=resp
           // console.log(resp)
             handleRow(resp) 
            //update dispense

        })
        .catch((err)=>{
            console.log(err)
        })

        //update status(billed) + action()
        //?attached chosen product to medication
        //dispense helper?
         setName("")
         setBaseunit("")
         setQuantity("")
         setInventoryId("")
         setSellingPrice("")
         setInvQuantity("")
             handleAmount()
        // setCalcAmount(null)
        await setSuccess(true)
        getSearchfacility(false)
        setObj("")
        /* console.log(success)
        console.log(qamount)
        console.log(productItem) */
        setChangeAmount(true)

     }


     const handleQtty=async(e)=>{
        /*  if (invquantity<e.target.value){
             toast({
                 message: 'You can not sell more quantity than exist in inventory ' ,
                 type: 'is-danger',
                 dismissible: true,
                 pauseOnHover: true,
               })
             return
         } */
         setQuantity(e.target.value)
         if (e.target.vlue===""){
            setQuantity(1)
         }
        /*  calcamount1=quantity*sellingprice
         await setCalcAmount(calcamount1) */
         //console.log(calcamount)
     }

     useEffect( () => {
          setProductEntry({

             date,
             documentNo,
             type,
             totalamount,
             source,
         })
        setCalcAmount(quantity*sellingprice) 
         return () => {

         }
     },[date])

     const resetform=()=>{
      setType("Sales")
     setDocumentNo("")
     setTotalamount("")
     setProductId("")
     setSource("")
     setDate("")
     setName("")
     setBaseunit()
     setCostprice()
     setProductItem([])
     }


     const handleAdmit= async(e)=>{ //handle admit

    //alert("something")
    //update order
    //create admission resource
    //?create bill?

    const note={
       Note:"Patient Admitted to " + state.employeeLocation.locationName + " " + state.employeeLocation.locationType + " at " +  Date().toLocaleString(),
       Instruction: medication.instruction
    }

    //console.log(note)
    let document={}
    // data.createdby=user._id
    // console.log(data);
        if (user.currentEmployee){
        document.facility=user.currentEmployee.facilityDetail._id 
        document.facilityname=user.currentEmployee.facilityDetail.facilityName // or from facility dropdown
        }
        document.documentdetail=note
        console.log(document.documentdetail)
        document.documentname="Admission" //state.DocumentClassModule.selectedDocumentClass.name
        // document.documentClassId=state.DocumentClassModule.selectedDocumentClass._id
        document.location=state.employeeLocation.locationName+" "+state.employeeLocation.locationType
        document.locationId=state.employeeLocation.locationId
        document.client=medication.client._id
        document.clientname=medication.client.firstname+ " "+ medication.client.middlename+" "+ medication.client.lastname
        document.clientobj=medication.client
        document.createdBy=user._id
        document.createdByname=user.firstname+ " "+user.lastname
        document.status="completed"


    const    data={
              //  encounter_id:{type: Schema.Types.ObjectId,},
              //  hospitalization_id:{type: Schema.Types.ObjectId,},
                //order
                order:medication,
                order_id:medication._id,

                //location: ward/bed
                ward_name:state.WardModule.selectedWard.typeName,
                ward_id:state.WardModule.selectedWard._id,
                bed:bedObject.typeName,
                bed_id:bedObject._id,
                facility:user.currentEmployee.facilityDetail._id,
                //billing:perpertuity
                /* bill:{type:Schema.Types.Mixed},
                bill_id:{type: Schema.Types.ObjectId,}, */
                //status
                status:"occupied",
                //client
                client:medication.client,
                client_id:medication.clientId,
                //careteam
                admissionhx:[],
                careteam:[],
                start_time: new Date(),
                //end_time:"",
                createdby:user._id,
                documentation:document
                }

                e.preventDefault();
                setMessage("")
              //  setError(false)
                setSuccess(false)
                 // data.createdby=user._id
                  console.log(data);
                /*   if (user.currentEmployee){
                 data.facility=user.currentEmployee.facilityDetail._id  // or from facility dropdown
                  } */
                 // data.locationType="Front Desk"
                  AdmissionServ.create(data)
                .then(async (res)=>{
                        //console.log(JSON.stringify(res))
                       // e.target.reset();
                       /*  setMessage("Created Clinic successfully") */
                        setSuccess(true)
                        toast({
                            message: 'Admission successfull',
                            type: 'is-success',
                            dismissible: true,
                            pauseOnHover: true,
                          })
                          setSuccess(false)
                          setChosenBed()                        
                          setBedObject()
                          const    newProductEntryModule={
                            selectedAdmission:{},
                            show :''
                        }
                      await setState((prevstate)=>({...prevstate, AdmissionModule:newProductEntryModule}))
                    })
                    .catch((err)=>{
                        toast({
                            message: 'Error creating Admission ' + err,
                            type: 'is-danger',
                            dismissible: true,
                            pauseOnHover: true,
                          })
                    }) 
    }


    const handleBed=(e)=>{
        //console.log(JSON.parse(e.target.value))
       setChosenBed(e.target.value)
        let simpa= physicalbeds.find(obj=>obj._id===e.target.value)
        setBedObject(simpa)

    }

     const onSubmit = async(e) =>{
         e.preventDefault();
         setMessage("")
         //setError(false)
         setSuccess(false)
         await setProductEntry({

             date,
             documentNo,
             type,
             totalamount,
             source,
         })
         productEntry.productitems=productItem
         productEntry.createdby=user._id
         productEntry.transactioncategory="debit"
