/* eslint-disable */
import React, {useState,useContext, useEffect,useRef} from 'react'
import client from '../../feathers'
import {DebounceInput} from 'react-debounce-input';
import { useForm } from "react-hook-form";
//import {useHistory} from 'react-router-dom'
import {UserContext,ObjectContext} from '../../context'
import documentListForm from "../clientForm/documentList";
import chartListForm from "../clientForm/chartList";
import {toast} from 'bulma-toast'
// eslint-disable-next-line
const searchfacility={};


export default function DocumentClass() {
    const {state}=useContext(ObjectContext) //,setState
    // eslint-disable-next-line
    const [selectedDocumentClass,setSelectedDocumentClass]=useState()
    //const [showState,setShowState]=useState() //create|modify|detail

    return(
        <section className= "section remPadTop">
           {/*  <div className="level">
            <div className="level-item"> <span className="is-size-6 has-text-weight-medium">DocumentClass  Module</span></div>
            </div> */}
            <div className="columns ">
            <div className="column is-8 ">
                <DocumentClassList />
                </div>
            <div className="column is-4 ">
                {(state.DocumentClassModule.show ==='create')&&<DocumentClassCreate />}
                {(state.DocumentClassModule.show ==='detail')&&<DocumentClassDetail  />}
                {(state.DocumentClassModule.show ==='modify')&&<DocumentClassModify DocumentClass={selectedDocumentClass} />}

            </div>

            </div>                            
            </section>

    )

}

export function DocumentClassCreate(){
    const { register, handleSubmit,setValue} = useForm(); //, watch, errors, reset 
    const [error, setError] =useState(false)
    const [success, setSuccess] =useState(false)
    const [message,setMessage] = useState("")
    // eslint-disable-next-line
    const [facility,setFacility] = useState()
    const DocumentClassServ=client.service('documentclass')
    //const history = useHistory()
    const {user} = useContext(UserContext) //,setUser
    // eslint-disable-next-line
    const [currentUser,setCurrentUser] = useState()



    const getSearchfacility=(obj)=>{ // buble-up from inputsearch for creating resource

        setValue("facility", obj._id,  {
            shouldValidate: true,
            shouldDirty: true
        })
    }

    useEffect(() => {
        setCurrentUser(user)
        //console.log(currentUser)
        return () => {

        }
    }, [user])

  //check user for facility or get list of facility  
    useEffect(()=>{
        //setFacility(user.activeDocumentClass.FacilityId)//
      if (!user.stacker){
         // console.log(currentUser)
        setValue("facility", user.currentEmployee.facilityDetail._id,  {
            shouldValidate: true,
            shouldDirty: true
        }) 
      }
    })

    const onSubmit = (data,e) =>{
        e.preventDefault();
        setMessage("")
        setError(false)
        setSuccess(false)
         // data.createdby=user._id
          console.log(data);
          if (user.currentEmployee){
         data.facility=user.currentEmployee.facilityDetail._id  // or from facility dropdown
          }
          data.locationType="DocumentClass"
        DocumentClassServ.create(data)
        .then((res)=>{
                //console.log(JSON.stringify(res))
                e.target.reset();
               /*  setMessage("Created DocumentClass successfully") */
                setSuccess(true)
                toast({
                    message: 'DocumentClass created succesfully',
                    type: 'is-success',
                    dismissible: true,
                    pauseOnHover: true,
                  })
                  setSuccess(false)
            })
            .catch((err)=>{
                toast({
                    message: 'Error creating DocumentClass ' + err,
                    type: 'is-danger',
                    dismissible: true,
                    pauseOnHover: true,
                  })
            })

      } 

    return (
        <>
            <div className="card ">
            <div className="card-header">
                <p className="card-header-title">
                    Create DocumentClass
                </p>
            </div>
            <div className="card-content vscrollable">

            <form onSubmit={handleSubmit(onSubmit)}>

                <div className="field">
                    <p className="control has-icons-left has-icons-right">
                    <input className="input is-small" ref={register({ required: true })}  name="name" type="text" placeholder="Name of DocumentClass" />
                    <span className="icon is-small is-left">
                        <i className="fas fa-map-signs"></i>
                    </span>

                </p>
            </div>

           <div className="field"  style={ !user.stacker?{display:"none"}:{}} >
                <InputSearch  getSearchfacility={getSearchfacility} clear={success} /> 
                <p className="control has-icons-left " style={{display:"none"}}>
                    <input className="input is-small" ref={register ({ required: true }) } name="facility" type="text" placeholder="Facility" />
                    <span className="icon is-small is-left">
                    <i className="fas  fa-map-marker-alt"></i>
                    </span>
                </p>
            </div>

            <div className="field">
                <p className="control">
                    <button className="button is-success is-small">
                        Create
                    </button>
                </p>
            </div>

            </form>
            </div>
            </div>
        </>
    )

}

export function DocumentClassList({standalone,closeModal}){
   // const { register, handleSubmit, watch, errors } = useForm();
    // eslint-disable-next-line
    const [error, setError] =useState(false)
     // eslint-disable-next-line
    const [success, setSuccess] =useState(false)
     // eslint-disable-next-line
   const [message, setMessage] = useState("") 
    const DocumentClassServ=client.service('documentclass')
    //const history = useHistory()
   // const {user,setUser} = useContext(UserContext)
    const [facilities,setFacilities]=useState([])
     // eslint-disable-next-line
   const [selectedDocumentClass, setSelectedDocumentClass]=useState() //
    // eslint-disable-next-line
    const {state,setState}=useContext(ObjectContext)
    // eslint-disable-next-line
    const {user,setUser}=useContext(UserContext)



    let classList = [...facilities];
   /*  classList.push(...documentListForm); */

    const handleCreateNew = async()=>{
        const    newDocumentClassModule={
            selectedDocumentClass:{},
            show :'create'
            }
       await setState((prevstate)=>({...prevstate, DocumentClassModule:newDocumentClassModule}))
       //console.log(state)


    }
    const handleRow= async(DocumentClass)=>{
        //console.log("b4",state)

        //console.log("handlerow",DocumentClass)

        await setSelectedDocumentClass(DocumentClass)

        const    newDocumentClassModule={
            selectedDocumentClass:DocumentClass,
            show :'detail'
        }
       await setState((prevstate)=>({...prevstate, DocumentClassModule:newDocumentClassModule}))
       //console.log(state)
       if (standalone){
           closeModal()
       }

    }

   const handleSearch=(val)=>{
       const field='name'
       console.log(val)
       DocumentClassServ.find({query: {
                [field]: {
                    $regex:val,
                    $options:'i'

                },
