   // eslint-disable-next-line 
   const [chosen,setChosen]=useState(false)
   // eslint-disable-next-line 
   const [count,setCount]=useState(0)
   const inputEl=useRef(null)
   const [val,setVal]=useState("")
    const [productModal,setProductModal]=useState(false)

   const handleRow= async(obj)=>{
        await setChosen(true)
        await setSimpa(obj.name)
       // alert("something is chaning")
        //console.log(obj)
       getSearchService(obj)



        // setSelectedFacility(obj)
        setShowPanel(false)
        await setCount(2)
        /* const    newfacilityModule={
            selectedFacility:facility,
            show :'detail'
        }
   await setState((prevstate)=>({...prevstate, facilityModule:newfacilityModule})) */
   //console.log(state)
}
    const handleBlur=async(e)=>{
         if (count===2){
             console.log("stuff was not chosen")
         }

       /*  console.log("blur")
         setShowPanel(false)
        console.log(JSON.stringify(simpa))
        if (simpa===""){
            console.log(facilities.length)
            setSimpa("abc")
            setSimpa("")
            setFacilities([])
            inputEl.current.setValue=""
        }
        console.log(facilities.length)
        console.log(inputEl.current) */
    }
    const handleSearch=async(value)=>{
        setVal(value)
        if (value===""){
            setShowPanel(false)
            getSearchService(false)
            await setFacilities([])
            return
        }
        const field='name' //field variable


        if (value.length>=3 ){
            productServ.find({query: {     //service
                 [field]: {
                     $regex:value,
                     $options:'i'

                 },
                 $limit:10,
                 $sort: {
                     createdAt: -1
                   }
                     }}).then((res)=>{
             // console.log("product  fetched successfully") 
              //console.log(res.data) 
              //...new Set(array)

              const uniqueArr = [...new Set(res.data.map(data => data.name ))]
             // console.log(uniqueArr)
              let uniqueObj=[]
              uniqueArr.forEach((obj)=>{
                uniqueObj.push((res.data.find((el)=>el.name==obj)))
              //  console.log(uniqueObj)
              })
              //console.log(uniqueObj)
                setFacilities(uniqueObj)
                 setSearchMessage(" product  fetched successfully")
                 setShowPanel(true)
             })
             .catch((err)=>{
                toast({
                    message: 'Error creating Services ' + err,
                    type: 'is-danger',
                    dismissible: true,
                    pauseOnHover: true,
                  })
             })
         }
        else{
           // console.log("less than 3 ")
            //console.log(val)
            setShowPanel(false)
            await setFacilities([])
            //console.log(facilities)
        }
    }

    const handleAddproduct =(val)=>{
        setProductModal(true) 
        setShowPanel(false)
        notfound({
            status:true,
            name:val
            })
    }
    const handlecloseModal =()=>{
        setProductModal(false)
        handleSearch(val)
    }
    useEffect(() => {
       if (clear){

           console.log("success has changed",clear)
           setSimpa("")


       }
        return () => {

        }
    }, [clear] )


    return (
        <div>
            <div className="field">
                <div className="control has-icons-left  ">
                    <div className={`dropdown ${showPanel?"is-active":""}`}  style={{width:"100%"}}>
                        <div className="dropdown-trigger"  style={{width:"100%"}}>
                            <DebounceInput className="input is-small " 
                                type="text" placeholder="Search Services"
                                value={simpa}
                                minLength={3}
                                debounceTimeout={400}
                                onBlur={(e)=>handleBlur(e)}
                                onChange={(e)=>handleSearch(e.target.value)}
                                inputRef={inputEl}
                                  />
                            <span className="icon is-small is-left">
                                <i className="fas fa-search"></i>
                            </span>
                        </div>
                        {/* {searchError&&<div>{searchMessage}</div>} */}
                        <div className="dropdown-menu"   style={{width:"100%"}}>
                            <div className="dropdown-content">
                          { facilities.length>0?"":<div className="dropdown-item" onClick={()=>handleAddproduct(val)}> <span>Add {val} to service list</span>  </div>}

                              {facilities.map((facility, i)=>(

                                    <div className="dropdown-item" key={facility._id} onClick={()=>handleRow(facility)}>

                                        <span>{facility.name} ({facility.category})</span>

                                    </div>

                                    ))}

                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
