/* eslint-disable */
import React, {useState,useContext, useEffect,useRef} from 'react'
import client from '../../feathers'
import {DebounceInput} from 'react-debounce-input';
import { useForm } from "react-hook-form";
//import {useHistory} from 'react-router-dom'
import {UserContext,ObjectContext} from '../../context'
import FacilityPopup from '../helpers/FacilityPopup'
import {toast} from 'bulma-toast'
import {format, formatDistanceToNowStrict } from 'date-fns'
/* import {ProductCreate} from './Products' */
// eslint-disable-next-line
const searchfacility={};


export default function RadiologyOrders() {
    const {state}=useContext(ObjectContext) //,setState
    // eslint-disable-next-line
    const [selectedProductEntry,setSelectedProductEntry]=useState()
    //const [showState,setShowState]=useState() //create|modify|detail

    return(
        <section className= "section remPadTop">
           {/*  <div className="level">
            <div className="level-item"> <span className="is-size-6 has-text-weight-medium">ProductEntry  Module</span></div>
            </div> */}
            <div className="columns ">
            <div className="column is-6 ">
                <RadiologyOrdersList />
                </div>
            <div className="column is-6 ">
                {(state.OrderModule.show ==='create')&&<RadiologyOrdersCreate />}
              {/*   {(state.OrderModule.show ==='detail')&&<ProductEntryDetail  />}
                {(state.OrderModule.show ==='modify')&&<ProductEntryModify ProductEntry={selectedProductEntry} />} */}

            </div>

            </div>                            
            </section>

    )

}

export function RadiologyOrdersCreate(){
   // const { register, handleSubmit,setValue} = useForm(); //, watch, errors, reset 
    const [error, setError] =useState(false)
    const [success, setSuccess] =useState(false)
    const [message,setMessage] = useState("")
    // eslint-disable-next-line
    const [facility,setFacility] = useState()
    const ProductEntryServ=client.service('productentry')
    //const history = useHistory()
    const {user} = useContext(UserContext) //,setUser
    // eslint-disable-next-line
    const [currentUser,setCurrentUser] = useState()
    const [type,setType] = useState("Purchase Invoice")
    const [documentNo,setDocumentNo] = useState("")
    const [totalamount,setTotalamount] = useState("")
    const [productId,setProductId] = useState("")
    const [source,setSource] = useState("")
    const [date,setDate] = useState("")
    const [name,setName] = useState("")
    const [destination,setDestination] = useState('')
    const [destinationId,setDestinationId] = useState('')
    const [destinationModal,setDestinationModal] = useState(false)
    const [test,setTest] = useState()
    const [instruction,setInstruction] = useState()
    const [productItem,setProductItem] = useState([])
    const {state}=useContext(ObjectContext)
    const ClientServ=client.service('clinicaldocument')

    const [productEntry,setProductEntry]=useState({
        productitems:[],
        date,
        documentNo,
        type,
        totalamount,
        source,

    })

    const handlecloseModal =()=>{
        setDestinationModal(false)
        //handleSearch(val)
    }
    const productItemI={
      /*   productId,
        name, */
        test,
        destination,
        instruction,
        destinationId
        /* costprice,
        amount:quantity*costprice,
        baseunit */

    }
    // consider batchformat{batchno,expirydate,qtty,baseunit}
    //consider baseunoit conversions
    const getSearchfacility=(obj)=>{

        if (!obj){
            //"clear stuff"
            setInstruction("")
            setTest("")

        }
        setInstruction(obj.instruction)
        setTest(obj.test)
       // setBaseunit(obj.baseunit)

       /*  setValue("facility", obj._id,  {
            shouldValidate: true,
            shouldDirty: true
        }) */
    }

    useEffect(() => {
        setCurrentUser(user)
        //console.log(currentUser)
        return () => {

        }
    }, [user])
   /*  useEffect(() => {
        setProductItem(
            prevProd=>prevProd.concat(productItemI)
        )
        console.log(productItem)
        return () => {

        }
    },[productItemI])
 */
    useEffect(() => {

        setDestination(state.DestinationModule.selectedDestination.facilityName)
        setDestinationId(state.DestinationModule.selectedDestination._id)
        return () => {

        }
    }, [state.DestinationModule.selectedDestination])

    const handleChangeType=async (e)=>{
        await setType(e.target.value)
    }
    const handleClickProd=async()=>{
        await setSuccess(false)
        if (!(productItemI.test && productItemI.test.length>0 )){
            toast({
                message: 'Test can not be empty ' ,
                type: 'is-danger',
                dismissible: true,
                pauseOnHover: true,
              })
              return
        }
        await setProductItem(
            prevProd=>prevProd.concat(productItemI)
        )
        setName("")
        setTest("")
        setInstruction("")
        setDestination( user.currentEmployee.facilityDetail.facilityName)
        setDestinationId( user.currentEmployee.facilityDetail._id)
       // setDestination("")
       await setSuccess(true)
       console.log(success)
       console.log(productItem)
    }
  //check user for facility or get list of facility  
   /*  useEffect(()=>{
        //setFacility(user.activeProductEntry.FacilityId)//
      if (!user.stacker){
          console.log(currentUser)
           /* setValue("facility", user.currentEmployee.facilityDetail._id,  {
            shouldValidate: true,
            shouldDirty: true
        })  

      }
    }) */

    const handleChangeDestination=()=>{
        setDestinationModal(true)
    }

    const resetform=()=>{
     setType("Purchase Invoice")
    setDocumentNo("")
    setTotalamount("")
    setProductId("")
    setSource("")
    setDate("")
    setName("")
    setTest("")
    setInstruction("")
    setProductItem([])
    }
    const onSubmit = () =>{
        //data,e
       // e.preventDefault();
        setMessage("")
        setError(false)
        setSuccess(false)
        //write document
        let document={}
         // data.createdby=user._id
         // console.log(data);
          if (user.currentEmployee){
          document.facility=user.currentEmployee.facilityDetail._id 
          document.facilityname=user.currentEmployee.facilityDetail.facilityName // or from facility dropdown
          }
         document.documentdetail=productItem
         console.log(document.documentdetail)
          document.documentname="Radiology Orders" //state.DocumentClassModule.selectedDocumentClass.name
         // document.documentClassId=state.DocumentClassModule.selectedDocumentClass._id
         document.location=state.employeeLocation.locationName+" "+state.employeeLocation.locationType
         document.locationId=state.employeeLocation.locationId
          document.client=state.ClientModule.selectedClient._id
          document.clientname=state.ClientModule.selectedClient.firstname+ " "+state.ClientModule.selectedClient.middlename+" "+state.ClientModule.selectedClient.lastname
          document.clientobj=state.ClientModule.selectedClient
          document.createdBy=user._id
          document.createdByname=user.firstname+ " "+user.lastname
          document.status="completed"
          console.log(document)
        ClientServ.create(document)
        .then((res)=>{
                //console.log(JSON.stringify(res))
               // e.target.reset();
               /*  setMessage("Created Client successfully") */
                setSuccess(true)
                toast({
                    message: 'Radiology Order created succesfully',
                    type: 'is-success',
                    dismissible: true,
                    pauseOnHover: true,
                  })
                  setSuccess(false)
                  setProductItem([])
            })
            .catch((err)=>{
                toast({
                    message: 'Error creating Radiology Orders ' + err,
                    type: 'is-danger',
                    dismissible: true,
                    pauseOnHover: true,
                  })
            })

      } 

    useEffect(() => {
        setDestination( user.currentEmployee.facilityDetail.facilityName)
        setDestinationId( user.currentEmployee.facilityDetail._id)
        return () => {

        }
    }, [])

    return (
        <>
            <div className="card card-overflow">
            <div className="card-header">
                <p className="card-header-title">
                    Create Radiology Orders
                </p>
            </div>
            <div className="card-content ">

           {/*  <form onSubmit={onSubmit}> {/* handleSubmit(onSubmit)  </form>  */}



         {/* array of ProductEntry items */}

        <label className="label is-small">Add Test:</label>
         <div className="field is-horizontal">
            <div className="field-body">
             <div className="field is-expanded"  /* style={ !user.stacker?{display:"none"}:{}} */  > 
                    <TestHelperSearch  getSearchfacility={getSearchfacility} clear={success} />  
                  <p className="control has-icons-left " style={{display:"none"}}>
                        <input className="input is-small"  /* ref={register ({ required: true }) }  */   value={test} name="test" type="text" onChange={e=>setTest(e.target.value)} placeholder="test" />
                        <span className="icon is-small is-left">
                        <i className="fas  fa-map-marker-alt"></i>
                        </span>
                    </p> 
                </div>
               {/*  <div className="field">
                <p className="control has-icons-left">
                    <input className="input is-small" ref={register({ required: true })} name="test" value={test} type="text" onChange={e=>setTest(e.target.value)} placeholder="test"  />
                    <span className="icon is-small is-left">
                    <i className="fas fa-envelope"></i>
                    </span>
                </p>

            </div>  */}


            <div className="field">
            <p className="control">
                    <button className="button is-info is-small  is-pulled-right">
                      <span className="is-small" onClick={handleClickProd}> +</span>
                    </button>
                </p>
            </div>
            </div>
         </div>
         <div className="field is-horizontal">
            <div className="field-body">
         {/* <div className="field">
                <p className="control has-icons-left">
                    <input className="input is-small"  ref={register({ required: true })}  name="instruction" value={instruction} type="text" onChange={e=>setInstruction(e.target.value)} placeholder="Instructions/Note"  />
                    <span className="icon is-small is-left">
                    <i className="fas fa-envelope"></i>
                    </span>
                </p>

            </div>   */}
            <div className="field">
                <p className="control has-icons-left">
                    <input className="input is-small " disabled /* ref={register({ required: true })} */ name="destination" value={destination===user.currentEmployee.facilityDetail.facilityName?"In-house":destination} type="text" onChange={e=>setDestination(e.target.value)} placeholder="Destination Pharmacy"  />
                    <span className="icon is-small is-left">
                    <i className="fas fa-envelope"></i>
                    </span>
                </p>
                <button className="button is-small is-success btnheight" onClick={handleChangeDestination}>Change</button>

            </div>  
            </div> 
            </div> 

       {(productItem.length>0) && <div>
            <label className="label is-size-7">Lab Orders:</label>
         <table className="table is-striped  is-hoverable is-fullwidth is-scrollable ">
                <thead>
                    <tr>
                    <th><abbr title="Serial No">S/No</abbr></th>
                   {/*  <th><abbr title="Type">Name</abbr></th> */}
                    <th><abbr title="Test">Test</abbr></th>
                    <th><abbr title="Destination">Destination</abbr></th>
                     {/*<th><abbr title="Cost Price">Cost Price</abbr></th>
                    <th><abbr title="Cost Price">Amount</abbr></th> */}
                    <th><abbr title="Actions">Actions</abbr></th>
                    </tr>
                </thead>
                <tfoot>

                </tfoot>
                <tbody>
                   { productItem.map((ProductEntry, i)=>(

                        <tr key={i}>
                        <th>{i+1}</th>
                        {/* <td>{ProductEntry.name}</td> */}
                        <td>{ProductEntry.test}<br/>
                        <span className="help">{ProductEntry.instruction}</span></td>
                       <td>{ProductEntry.destination===user.currentEmployee.facilityDetail.facilityName?"In-house":ProductEntry.destination}</td>
                         {/* <td>{ProductEntry.costprice}</td>
                        <td>{ProductEntry.amount}</td> */}
                        <td><span className="showAction"  >x</span></td>

                        </tr>

                    ))}
                </tbody>
                </table>
                <div className="field mt-2">
                <p className="control">
                    <button className="button is-success is-small" disabled={!productItem.length>0} onClick={onSubmit}>
                        Create
                    </button>
                </p>
                </div>
                </div>

            }


            </div>
            </div>
            <div className={`modal ${destinationModal?"is-active":""}` }>
                                    <div className="modal-background"></div>
                                    <div className="modal-card">
                                        <header className="modal-card-head">
                                        <p className="modal-card-title">Choose Destination</p>
                                        <button className="delete" aria-label="close"  onClick={handlecloseModal}></button>
                                        </header>
                                        <section className="modal-card-body">
                                            <FacilityPopup facilityType="Laboratory"  closeModal={handlecloseModal}/>
                                        {/* <StoreList standalone="true" /> */}
                                       {/*  <ProductCreate /> */}
                                        </section>
                                        {/* <footer className="modal-card-foot">
                                        <button className="button is-success">Save changes</button>
                                        <button className="button">Cancel</button>
                                        </footer> */}
                                    </div>
                                </div>       
        </>
    )

}

export function RadiologyOrdersList({standalone}){
   // const { register, handleSubmit, watch, errors } = useForm();
    // eslint-disable-next-line
    const [error, setError] =useState(false)
     // eslint-disable-next-line
    const [success, setSuccess] =useState(false)
     // eslint-disable-next-line
   const [message, setMessage] = useState("") 
    const OrderServ=client.service('order')
    //const history = useHistory()
   // const {user,setUser} = useContext(UserContext)
    const [facilities,setFacilities]=useState([])
     // eslint-disable-next-line
   const [selectedOrder, setSelectedOrder]=useState() //
    // eslint-disable-next-line
    const {state,setState}=useContext(ObjectContext)
    // eslint-disable-next-line
    const {user,setUser}=useContext(UserContext)



    const handleCreateNew = async()=>{
        const    newProductEntryModule={
            selectedOrder:{},
            show :'create'
            }
       await setState((prevstate)=>({...prevstate, OrderModule:newProductEntryModule}))
       //console.log(state)


    }
    const handleDelete=(doc)=>{
        // console.log(doc)
         let confirm = window.confirm(`You are about to delete a ${doc.order} Radiology order?`)
         if (confirm){
        OrderServ.remove(doc._id)
         .then((res)=>{
             toast({
                 message: 'Radiology order deleted succesfully',
                 type: 'is-success',
                 dismissible: true,
                 pauseOnHover: true,
               })
               setSuccess(false)
         })
         .catch((err)=>{
             toast({
                 message: 'Error deleting Radiology order' + err,
                 type: 'is-danger',
                 dismissible: true,
                 pauseOnHover: true,
               })
         })
      }
     }
    const handleRow= async(ProductEntry)=>{
        //console.log("b4",state)

        //console.log("handlerow",ProductEntry)

        await setSelectedOrder(ProductEntry)

        const    newProductEntryModule={
            selectedOrder:ProductEntry,
            show :'detail'
