import React, {useState,useContext, useEffect,useRef} from 'react'
import {  useRouteMatch,  useHistory} from 'react-router-dom' //Route, Switch,Link, NavLink,
import client from '../../feathers'
import {DebounceInput} from 'react-debounce-input';
import { useForm } from "react-hook-form";
//import {useHistory} from 'react-router-dom'
import {UserContext,ObjectContext} from '../../context'
import {toast} from 'bulma-toast'
import { formatDistanceToNowStrict, format } from 'date-fns'
import ClientFinInfo from './ClientFinInfo';
import BillServiceCreate from '../Finance/BillServiceCreate'
import {AppointmentCreate} from '../Clinic/Appointments'
import InfiniteScroll from "react-infinite-scroll-component";
import ClientBilledPrescription from '../Finance/ClientBill'
import ClientGroup from './ClientGroup';
import DatePicker from "react-datepicker";

import "react-datepicker/dist/react-datepicker.css";
import {OrgFacilitySearch, SponsorSearch} from '../helpers/FacilitySearch'
var random = require('random-string-generator');
// eslint-disable-next-line
const searchfacility={};


export default function Policy() {
    const {state}=useContext(ObjectContext) //,setState
    // eslint-disable-next-line
    const [selectedClient,setSelectedClient]=useState()
    //const [showState,setShowState]=useState() //create|modify|detail

    return(
        <section className= "section remPadTop">
            <div className="columns ">
            <div className="column is-6 ">
                <PolicyList />
                </div>
            <div className="column is-6 ">
            {/*   {(state.ManagedCareModule.show ==='List')&&<PolicyList />} */}
                {(state.ManagedCareModule.show ==='create')&&<PolicyCreate />} 
               {/*  {(state.ClientModule.show ==='detail')&&<ClientDetail  />}
                {(state.ClientModule.show ==='modify')&&<ClientModify Client={selectedClient} />}  */}
            </div>
            </div>                            
            </section>

    )

}



export function PolicyList(){
    // const { register, handleSubmit, watch, errors } = useForm();
     // eslint-disable-next-line
     const [error, setError] =useState(false)
      // eslint-disable-next-line
     const [success, setSuccess] =useState(false)
      // eslint-disable-next-line
    const [message, setMessage] = useState("") 
     const ClientServ=client.service('policy')
     //const history = useHistory()
    // const {user,setUser} = useContext(UserContext)
     const [facilities,setFacilities]=useState([])
      // eslint-disable-next-line
    const [selectedClient, setSelectedClient]=useState() //
     // eslint-disable-next-line
     const {state,setState}=useContext(ObjectContext)
     // eslint-disable-next-line
     const {user,setUser}=useContext(UserContext)
     const [page, setPage] = useState(0) 
     const [limit, setLimit] = useState(50) 
     const [total, setTotal] = useState(0) 



     const handleCreateNew = async()=>{
         const newClientModule={
             selectedClient:{},
             show :'create'
             }
         await setState((prevstate)=>({...prevstate, ClientModule:newClientModule}))
        //console.log(state)
         } 


     const handleRow= async(Client)=>{
         await setSelectedClient(Client)
         const    newClientModule={
             selectedClient:Client,
             show :'detail'
         }
        await setState((prevstate)=>({...prevstate, ClientModule:newClientModule}))
     }

    const handleSearch=(val)=>{
          // eslint-disable-next-line
        const field='firstname'
        console.log(val)
        ClientServ.find({query: {
                 $or:[
                     { firstname: {
                         $regex:val,
                         $options:'i' 
                     }},
                     { lastname: {
                         $regex:val,
                         $options:'i' 
                     }},
                     { middlename: {
                         $regex:val,
                         $options:'i' 
                     }},
                     { phone: {
                         $regex:val,
                         $options:'i' 
                     }},
                     { clientTags: {
                         $regex:val,
                         $options:'i' 
                     }},
                     { mrn: {
                         $regex:val,
                         $options:'i' 
                     }},
                     { email: {
                         $regex:val,
                         $options:'i' 
                     }},
                     { specificDetails: {
                         $regex:val,
                         $options:'i' 
                     }},
                     { gender: val},
                 ],

                 "relatedfacilities.facility":user.currentEmployee.facilityDetail._id, // || "",
                 $limit:limit,
                 $sort: {
                     createdAt: -1
                   }
                     }}).then((res)=>{
                 console.log(res)
                setFacilities(res.data)
                 setMessage(" Client  fetched successfully")
                 setSuccess(true) 
             })
             .catch((err)=>{
                 console.log(err)
                 setMessage("Error fetching Client, probable network issues "+ err )
                 setError(true)
             })
         }

     const getFacilities= async()=>{

             if (user.currentEmployee){ 
                // const findClient= await ClientServ.find()     
             const findClient= await ClientServ.find(
                 {query: {
                    // "relatedfacilities.facility":user.currentEmployee.facilityDetail._id,
                    // $limit:limit,
                    // $skip:page * limit,
                     $sort: {
                         createdAt: -1
                     }
                     }})
        /*  if (page===0){ */
             await setFacilities(findClient.data)
             console.log(findClient.data)
         /* }else{
             await setFacilities(prevstate=>prevstate.concat(findClient.data))
         } */


          await setTotal(findClient.total)
          //console.log(user.currentEmployee.facilityDetail._id, state)
          //console.log(facilities)
          setPage(page=>page+1)
                 }
                 else {
                     if (user.stacker){
                         const findClient= await ClientServ.find(
                             {query: {

                                 $limit:20,
                                 $sort: {
                                     createdAt: -1
                                 }
                                 }})

                     await setFacilities(findClient.data)

                     }
                 }
             }


     useEffect(() => {

                 if (user){
                     //getFacilities()
                     rest()
                 }else{
                     /* const localUser= localStorage.getItem("user")
                     const user1=JSON.parse(localUser)
                     console.log(localUser)
                     console.log(user1)
                     fetchUser(user1)
                     console.log(user)
                     getFacilities(user) */
                 }
                 ClientServ.on('created', (obj)=>rest ())
