/* eslint-disable */
import React, {useState,useContext, useEffect,useRef} from 'react'
import client from '../../feathers'
import {DebounceInput} from 'react-debounce-input';
import { useForm } from "react-hook-form";
//import {useHistory} from 'react-router-dom'
import {UserContext,ObjectContext} from '../../context'
import {toast} from 'bulma-toast'
import {FacilitySearch} from '../helpers/FacilitySearch'
import CategorySearch from "../helpers/CategorySearch"
import {
    Accordion,
    AccordionItem,
    AccordionItemHeading,
    AccordionItemState,
    AccordionItemButton,
    AccordionItemPanel,
} from 'react-accessible-accordion';


// Demo styles, see 'Styles' section below for some notes on use.
import 'react-accessible-accordion/dist/fancy-example.css';
import { StoreModify } from '../inventory/Store';
// eslint-disable-next-line
const searchfacility={};


export default function Services() {
    const {state}=useContext(ObjectContext) //,setState
    // eslint-disable-next-line
    const [selectedServices,setSelectedServices]=useState()
    //const [showState,setShowState]=useState() //create|modify|detail

    return(
        <section className= "section remPadTop">
           {/*  <div className="level">
            <div className="level-item"> <span className="is-size-6 has-text-weight-medium">Services  Module</span></div>
            </div> */}
            <div className="columns ">
            <div className="column is-6 ">
                <ServicesList />
                </div>
            <div className="column is-6 ">
                {(state.ServicesModule.show ==='create')&&<ServicesCreate />}
                {(state.ServicesModule.show ==='detail')&&<ServicesDetail  />}
                {(state.ServicesModule.show ==='modify')&&<ServicesModify Services={selectedServices} />}

            </div>

            </div>                            
            </section>

    )

}

export function ServicesCreate(){
   // const { register, handleSubmit,setValue} = useForm(); //, watch, errors, reset 
    const [categoryname, setCategoryName] =useState("")
    const [success, setSuccess] =useState(false)
    const [success2, setSuccess2] =useState(false)
    const [cash,setCash] = useState("Cash")
    // eslint-disable-next-line
    const [facility,setFacility] = useState()
    const ServicesServ=client.service('billing')
    //const history = useHistory()
    const {user} = useContext(UserContext) //,setUser
    // eslint-disable-next-line
    const [facilityId,setFacilityId] = useState("")
    const [source,setSource] = useState("")
    const [panel,setPanel] = useState(false)
    const [name,setName] = useState("")
    const [benefittingplans,setBenefittingPlans] = useState([])
    const [quantity,setQuantity] = useState()
    const [costprice,setCostprice] = useState("")
    const [orgType,setOrgType] = useState("")
    const [productItem,setProductItem] = useState([])
    const [plan,setPlan] = useState("")
    const [service,setService] = useState("")
    const [currentUser,setCurrentUser] = useState("")
    const [panelList,setPanelList] = useState([])
    const [successService,setSuccessService] = useState(false)
    const {state}=useContext(ObjectContext)
    const [chosen2, setChosen2]=useState()

    const [Services,setServices]=useState({
        productitems:[],
        panel,
        source,

    })

    let productItemI={
       facilityId,
        name,
        quantity,
        costprice,

    }
    // consider batchformat{batchno,expirydate,qtty,baseunit}
    //consider baseunoit conversions
    const getSearchfacility=(obj)=>{

        setFacilityId(obj._id)
        setName(obj.facilityName)
        setOrgType(obj.facilityType)
        if(!obj){
        setName("")
        setOrgType("")
        setFacilityId("")
        setCostprice("")

        }

       /*  setValue("facility", obj._id,  {
            shouldValidate: true,
            shouldDirty: true
        }) */
    }
    const getSearchfacility2=(obj)=>{

        setCategoryName(obj.categoryname)
        setChosen2(obj)

         if (!obj){
             //"clear stuff"
             setCategoryName("")
             setChosen2()

         }
     }

    const getSearchService=(obj)=>{
            setService(obj)
        if(!obj){
            setService("")
        }
            setSuccessService(false)
    }

    useEffect(() => {
        setCurrentUser(user)
        //console.log(currentUser)
        return () => {

        }
    }, [user])


    const handleClickProd=async()=>{
        if (productItem.length>0){

        if(!costprice || !name){
            toast({
                message: 'You need to enter organization name and price ' ,
                type: 'is-danger',
                dismissible: true,
                pauseOnHover: true,
              }) 
            return
        }
    }else{
        if(!costprice || !cash){
            toast({
                message: 'You need to enter organization name and price ' ,
                type: 'is-danger',
                dismissible: true,
                pauseOnHover: true,
              }) 
            return
        }

    }
        if(cash==="Cash"){
             productItemI={
                source_org:user.currentEmployee.facilityDetail._id,
                source_org_name:user.currentEmployee.facilityDetail.facilityName,
                dest_org:user.currentEmployee.facilityDetail._id,
                dest_org_name:user.currentEmployee.facilityDetail.facilityName,
                price:costprice,
                billing_type:"Cash",
                plans:benefittingplans
             } 
             await setCash("")
        }else{
            productItemI={
               source_org:facilityId, 
               source_org_name:name, 
               dest_org:user.currentEmployee.facilityDetail._id,
               dest_org_name:user.currentEmployee.facilityDetail.facilityName,
               price:costprice,
               billing_type:orgType==="HMO"?"HMO":"Company", 
               plans:benefittingplans

            } 
            await setCash("")
       }

        await setSuccess(false)
        setProductItem(
            prevProd=>prevProd.concat(productItemI)
        )
        setName("")
        setOrgType("")
        setFacilityId("")
        setCostprice("")
       await setSuccess(true)

    }


    const resetform=()=>{

    setFacilityId("")
    setSource("")
    setPanel(false)
    setName("")

    setCostprice("")
    setProductItem([])
    setCategoryName("")
   setService("")
    setPanelList([])
    setPlan("")
    setBenefittingPlans([])
    setCash("Cash")
    setOrgType("")
    }

    const onSubmit = async() =>{
       // e.preventDefault();
       if(panel && (panelList.length===0)){
        toast({
            message: "Please choose services that make up panel or uncheck panel ",
            type: 'is-danger',
            dismissible: true,
            pauseOnHover: true,
          })
          return   
       }

        setSuccess(false)
       let data ={
           name:source,
           category:categoryname,
           facility:user.currentEmployee.facilityDetail._id,
           facilityname:user.currentEmployee.facilityDetail.facilityName,
           panel:panel,
           panelServices:panelList,
           contracts:productItem,
           createdBy:user._id
       }

        ServicesServ.create(data)
        .then((res)=>{
                //console.log(JSON.stringify(res))
                resetform()
               /*  setMessage("Created Services successfully") */
                setSuccess(true)
                setSuccess2(true)
                toast({
                    message: 'Service created succesfully',
                    type: 'is-success',
                    dismissible: true,
                    pauseOnHover: true,
                  })
                  setSuccess(false)
