/* eslint-disable */
import React, {useState,useContext, useEffect,useRef} from 'react'
import {Route, Switch,  useRouteMatch, Link, NavLink, useHistory} from 'react-router-dom'
import client from '../../feathers'
import {DebounceInput} from 'react-debounce-input';
import { useForm } from "react-hook-form";
//import {useHistory} from 'react-router-dom'
import {UserContext,ObjectContext} from '../../context'
import {toast} from 'bulma-toast'
import { formatDistanceToNowStrict, format, subDays,addDays } from 'date-fns'
import DatePicker from "react-datepicker";
import LocationSearch from "../helpers/LocationSearch"
import EmployeeSearch from "../helpers/EmployeeSearch"
import BillServiceCreate from '../Finance/BillServiceCreate'
import "react-datepicker/dist/react-datepicker.css";
// eslint-disable-next-line
const searchfacility={};


export default function TheatreAppointments() {
    const {state}=useContext(ObjectContext) //,setState
    // eslint-disable-next-line
    const [selectedClient,setSelectedClient]=useState()
    const [selectedAppointment,setSelectedAppointment]=useState()
    //const [showState,setShowState]=useState() //create|modify|detail

    return(
        <section className= "section remPadTop">
            <div className="columns ">

            <div className="column is-8 ">
                <TheatreAppointmentList />
                </div>
            <div className="column is-4 ">
                {(state.AppointmentModule.show ==='create')&&<TheatreAppointmentCreate />}
                {(state.AppointmentModule.show ==='detail')&&<TheatreAppointmentDetail  />}
                {(state.AppointmentModule.show ==='modify')&&<TheatreAppointmentModify Client={selectedClient} />}

            </div>

            </div>                            
            </section>

    )

}

export function TheatreAppointmentCreate(){
    const {state,setState}=useContext(ObjectContext) 
    const { register, handleSubmit,setValue} = useForm(); //, watch, errors, reset 
    const [error, setError] =useState(false)
    const [success, setSuccess] =useState(false)
    const [success1, setSuccess1] =useState(false)
    const [success2, setSuccess2] =useState(false)
    const [message,setMessage] = useState("")
    const [clientId,setClientId] = useState()
    const [locationId,setLocationId] = useState()
    const [practionerId,setPractionerId] = useState()
    const [type,setType] = useState()
    // eslint-disable-next-line
    const [facility,setFacility] = useState()
    const ClientServ=client.service('appointments')
    //const history = useHistory()
    const {user} = useContext(UserContext) //,setUser
    // eslint-disable-next-line
    const [currentUser,setCurrentUser] = useState()
    const [selectedClient,setSelectedClient]=useState()
    const [selectedAppointment,setSelectedAppointment]=useState()
   // const [appointment_reason,setAppointment_reason]= useState()
    const [appointment_status,setAppointment_status]=useState("")
    const [appointment_type, setAppointment_type]=useState("")
    const [billingModal, setBillingModal] =useState(false)

    const [chosen, setChosen]=useState()
    const [chosen1, setChosen1]=useState()
    const [chosen2, setChosen2]=useState()
    const appClass=[ "On-site","Off-Site"]

    let appointee  //  =state.ClientModule.selectedClient 
   /*  const getSearchfacility=(obj)=>{
        setValue("facility", obj._id,  {
            shouldValidate: true,
            shouldDirty: true
        })
    } */
    const handleChangeType=async (e)=>{
        await setAppointment_type(e.target.value)
    }

    const handleChangeStatus=async (e)=>{
        await setAppointment_status(e.target.value)
    }


    const getSearchfacility=(obj)=>{

       setClientId(obj._id)
       setChosen(obj)
       //handleRow(obj)
        if (!obj){
            //"clear stuff"
            setClientId()
            setChosen()

        }
       /*  handleSelectedClient(obj) */

       /*  setValue("facility", obj._id,  {
            shouldValidate: true,
            shouldDirty: true
        }) */
    }
    const getSearchfacility1=(obj)=>{

        setLocationId(obj._id)
        setChosen1(obj)

         if (!obj){
             //"clear stuff"
             setLocationId()
             setChosen1()

         }
     }
     const getSearchfacility2=(obj)=>{

        setPractionerId(obj._id)
        setChosen2(obj)

         if (!obj){
             //"clear stuff"
             setPractionerId()
             setChosen2()

         }
     }

    useEffect(() => {
        setCurrentUser(user)
        //console.log(currentUser)
        return () => {

        }
    }, [user])

  //check user for facility or get list of facility  
    useEffect(()=>{
        //setFacility(user.activeClient.FacilityId)//
      if (!user.stacker){
       /*    console.log(currentUser)
        setValue("facility", user.currentEmployee.facilityDetail._id,  {
            shouldValidate: true,
            shouldDirty: true
        })  */
      }
    })

    const onSubmit = (data,e) =>{
        e.preventDefault();
        setMessage("")
        setError(false)
        setSuccess(false)
         // data.createdby=user._id
          console.log(data);
          if (user.currentEmployee){
          data.facility=user.currentEmployee.facilityDetail._id  // or from facility dropdown
          }
          data.locationId=locationId //state.ClinicModule.selectedClinic._id
          data.practitionerId=practionerId
          data.appointment_type=appointment_type
         // data.appointment_reason=appointment_reason
          data.appointment_status=appointment_status
          data.clientId=clientId
          data.firstname=chosen.firstname
            data.middlename=chosen.middlename
            data.lastname=chosen.lastname
            data.dob=chosen.dob
            data.gender=chosen.gender
            data.phone=chosen.phone
            data.email=chosen.email
            data.practitioner_name=chosen2.firstname+" "+chosen2.lastname
            data.practitioner_profession=chosen2.profession
            data.practitioner_department=chosen2.department
            data.location_name=chosen1.name
            data.location_type=chosen1.locationType
          data.actions=[{
              action:appointment_status,
              actor:user.currentEmployee._id
          }]
          console.log(data);


        ClientServ.create(data)
        .then((res)=>{
                //console.log(JSON.stringify(res))
                e.target.reset();
               setAppointment_type("")
               setAppointment_status("")
               setClientId("")
               setLocationId("")
               /*  setMessage("Created Client successfully") */
                setSuccess(true)
                setSuccess1(true)
                setSuccess2(true)
                toast({
                    message: 'Appointment created succesfully, Kindly bill patient if required',
                    type: 'is-success',
                    dismissible: true,
                    pauseOnHover: true,
                  })
                  setSuccess(false)
                  setSuccess1(false)
                  setSuccess2(false)
                 showBilling()
            })
            .catch((err)=>{
                toast({
                    message: 'Error creating Appointment ' + err,
                    type: 'is-danger',
                    dismissible: true,
                    pauseOnHover: true,
                  })
            })

      } 

   /*  useEffect(() => {
        getSearchfacility(state.ClientModule.selectedClient )


        return () => {

        }
    }, [state.ClientModule.selectedClient ]) */

    const handleSelectedClient= async(Client)=>{
        // await setSelectedClient(Client)
         const    newClientModule={
             selectedClient:Client,
             show :'detail'
         }
         console.log("something", Client)
         alert("looking for client")
        await setState((prevstate)=>({...prevstate, ClientModule:newClientModule}))
     }

  /*   const showBilling = () =>{
        setBillingModal(true)
       //history.push('/app/finance/billservice')
        }
        const  handlecloseModal1 = () =>{
            setBillingModal(false)
            }


            const handleRow= async(Client)=>{
              //  await setSelectedClient(Client)
                const    newClientModule={
                    selectedClient:Client,
                    show :'detail'
                }
               await setState((prevstate)=>({...prevstate, ClientModule:newClientModule}))
            } */
            const  handlecloseModal1 = () =>{
                setBillingModal(false)
                }
          const showBilling = () =>{
                setBillingModal(true)
               //history.push('/app/finance/billservice')
                }

    return (
        <>

            <div className="card ">
            <div className="card-header">
                <p className="card-header-title">
                    Create Appointment For Procedure
                </p>
            </div>
            <div className="card-content vscrollable remPad1">

            <form onSubmit={handleSubmit(onSubmit)}>
            <div className="field is-horizontal">
            <div className="field-body">
           {/*  <label className="label is-small mr-2">Client:</label> */}

          {state.ClientModule.selectedClient.firstname !==undefined ? <> 
              <label className="label is-size-7" > {state.ClientModule.selectedClient.firstname} {state.ClientModule.selectedClient .lastname}</label>
               </> 
               : <div className="field is-expanded"  /* style={ !user.stacker?{display:"none"}:{}} */ >
                    <ClientSearch  getSearchfacility={getSearchfacility} clear={success} /> 
                    <p className="control has-icons-left " style={{display:"none"}}>
                        <input className="input is-small"  /* ref={register ({ required: true }) } */  /* add array no */  value={clientId} name="ClientId" type="text" onChange={e=>setClientId(e.target.value)} placeholder="Product Id" />
                        <span className="icon is-small is-left">
                        <i className="fas  fa-map-marker-alt"></i>
                        </span>
                    </p>
                 {/* {sellingprice &&   "N"}{sellingprice} {sellingprice &&   "per"}  {baseunit} {invquantity} {sellingprice &&   "remaining"}  */}
                </div>}
            </div>
        </div>
        <div className="field is-horizontal">
            <div className="field-body">
                <div className="field is-expanded"  /* style={ !user.stacker?{display:"none"}:{}} */ >
                    <LocationSearch  getSearchfacility={getSearchfacility1} clear={success1} /> 
                    <p className="control has-icons-left " style={{display:"none"}}>
                        <input className="input is-small"  /* ref={register ({ required: true }) } */  /* add array no */  value={locationId} name="locationId" type="text" onChange={e=>setLocationId(e.target.value)} placeholder="Product Id" />
                        <span className="icon is-small is-left">
                        <i className="fas  fa-map-marker-alt"></i>
                        </span>
                    </p>
                </div>
            </div>
        </div>
        <div className="field is-horizontal">
            <div className="field-body">
                <div className="field is-expanded"  /* style={ !user.stacker?{display:"none"}:{}} */ >
                    <EmployeeSearch  getSearchfacility={getSearchfacility2} clear={success2} /> 
                    <p className="control has-icons-left " style={{display:"none"}}>
                        <input className="input is-small"  /* ref={register ({ required: true }) } */  /* add array no */  value={practionerId} name="practionerId" type="text" onChange={e=>setPractionerId(e.target.value)} placeholder="Product Id" />
                        <span className="icon is-small is-left">
                        <i className="fas  fa-map-marker-alt"></i>
                        </span>
                    </p>
                </div>

            </div>
        </div>
        {/* <div className="field is-horizontal">

                       <div className="field ml-3 ">

                         {
                               appClass.map((c,i) => 
                                   <label  className=" is-small" key={c}>
                                       <input type="radio" value={c} name="appointmentClass" ref={register} />{c + " "}
                                   </label>
                               )
                           }
                       </div>  
                       </div> */}
        <div className="field">
            <input name="start_time" ref={register ({ required: true })} type="datetime-local" />
        </div>

        <div className="field">    
                <div className="control">
                    <div className="select is-small">
                        <select name="type" value={type} onChange={handleChangeType}>
                           <option value="">Choose Appointment Type  </option>
                            <option value="New">New Procedure</option>
                            <option value="Followup">Repeat Procedure</option>

                           {/*  <option value="Readmission with 24hrs">Readmission with 24hrs</option>
                            <option value="Annual Checkup">Annual Checkup</option>
                            <option value="Walk in">Walk-in</option> */}
                        </select>
                    </div>
                </div>
            </div>
        <div className="field">    
                <div className="control">
                    <div className="select is-small">
                        <select name="appointment_status" value={appointment_status} onChange={handleChangeStatus}>
                           <option value="">Appointment Status  </option>
                            <option value="Scheduled">Scheduled</option>
                            <option value="Confirmed">Confirmed</option>
                            <option value="Billed">Billed</option>
                            <option value="Paid">Paid</option>
                            <option value="Checked In">Checked In</option>
                            <option value="Procedure in Progress">Procedure in Progress</option>
                            <option value="Completed Procedure">Completed Procedure</option>
                            <option value="Checked Out">Checked Out</option>
                            <option value="No Show">No Show</option>
                            <option value="Cancelled">Cancelled</option>

                        </select>
                    </div>
                </div>
        </div>
        <div className="field">
            <p className="control ">
                <textarea className="textarea is-small" ref={register()}  name="appointment_reason" type="text" placeholder="Surgical Procedure" />

            </p>
        </div>
        <div className="field">
            <p className="control ">
                <textarea className="textarea is-small" ref={register()}  name="information" type="text" placeholder="Other Information" />

            </p>
        </div>
        <div className="field " style={{display:"none"}} >
            <p className="control has-icons-left has-icons-right">
                <input className="input is-small" ref={register()}  name="billingservice" type="text" placeholder="Billing service" />
                <span className="icon is-small is-left">
                    <i className="fas fa-hospital"></i>
                </span>                    
            </p>
        </div>


        <div className="field  is-grouped mt-2" >
                <p className="control">
                    <button type="submit" className="button is-success is-small" >
                        Save
                    </button>
                </p>
                <p className="control">
                    <button className="button is-warning is-small" onClick={(e)=>e.target.reset()}>
                        Cancel
                    </button>
                </p>
                 {/* <p className="control">
                    <button className="button is-danger is-small" onClick={()=>handleDelete()} type="delete">
                       Delete
                    </button>
                </p> */}
            </div>

            </form>
            </div>
            </div>

             {/* <div className={`modal ${billingModal?"is-active":""}` }>
                <div className="modal-background"></div>
                <div className="modal-card">
                    <header className="modal-card-head">
                    <p className="modal-card-title">Bill Client</p>
                    <button className="delete" aria-label="close"  onClick={handlecloseModal1}></button>
                    </header>
                    <section className="modal-card-body">

                   { (!!state.ClientModule.client )&& <BillServiceCreate closeModal={handlecloseModal1}/>}
                    </section>

                </div>
            </div> */}               
        </>
    )

}

export function TheatreAppointmentList(){
   // const { register, handleSubmit, watch, errors } = useForm();
    // eslint-disable-next-line
    const [error, setError] =useState(false)
     // eslint-disable-next-line
    const [success, setSuccess] =useState(false)
     // eslint-disable-next-line
   const [message, setMessage] = useState("") 
    const ClientServ=client.service('appointments')
    //const history = useHistory()
   // const {user,setUser} = useContext(UserContext)
    const [facilities,setFacilities]=useState([])
     // eslint-disable-next-line
   const [selectedClient, setSelectedClient]=useState() //
    // eslint-disable-next-line
    const {state,setState}=useContext(ObjectContext)
    // eslint-disable-next-line
    const {user,setUser}=useContext(UserContext)
    const [startDate, setStartDate] = useState(new Date())
    const [selectedAppointment,setSelectedAppointment]=useState()



    const handleCreateNew = async()=>{
        const newClientModule={
            selectedAppointment:{},
            show :'create'
            }
        await setState((prevstate)=>({...prevstate, AppointmentModule:newClientModule}))
       //console.log(state)
       const newClient={
        selectedClient:{},
        show :'create'
        }
        await setState((prevstate)=>({...prevstate, ClientModule:newClient}))
        } 


    const handleRow= async(Client)=>{
        await setSelectedAppointment(Client)
        const    newClientModule={
            selectedAppointment:Client,
            show :'detail'
        }
       await setState((prevstate)=>({...prevstate, AppointmentModule:newClientModule}))
    }
    //console.log(state.employeeLocation)

   const handleSearch=(val)=>{
       const field='firstname'
     //  console.log(val)

     let query={
        $or:[
            { firstname: {
                $regex:val,
                $options:'i' 
            }},
            { lastname: {
                $regex:val,
                $options:'i' 
            }},
            { middlename: {
                $regex:val,
                $options:'i' 
            }}, 
            { phone: {
                $regex:val,
                $options:'i' 
            }},
            { appointment_type: {
                $regex:val,
                $options:'i' 
            }},
            { appointment_status: {
                $regex:val,
                $options:'i' 
            }},
            {appointment_reason: {
                $regex:val,
                $options:'i' 
            }},
            {location_type: {
                $regex:val,
                $options:'i' 
            }},
            {location_name: {
                $regex:val,
                $options:'i' 
            }},
            {practitioner_department: {
                $regex:val,
                $options:'i' 
            }},
            {practitioner_profession: {
                $regex:val,
                $options:'i' 
            }},
            {practitioner_name: {
                $regex:val,
                $options:'i' 
            }},

        ],
      facility:user.currentEmployee.facilityDetail._id, // || "",
        $limit:20,
        $sort: {
            createdAt: -1
          }
            }
          if (state.employeeLocation.locationType!=="Front Desk"){
              query.locationId=state.employeeLocation.locationId}  

       ClientServ.find({query: query
          }).then((res)=>{
                  console.log(res)
               setFacilities(res.data)
                setMessage(" Client  fetched successfully")
                setSuccess(true) 
            })
            .catch((err)=>{
                console.log(err)
                setMessage("Error fetching Client, probable network issues "+ err )
                setError(true)
            })
        }

    const getFacilities= async()=>{
            if (user.currentEmployee){   
                let stuff={
                    facility:user.currentEmployee.facilityDetail._id,
                   // locationId:state.employeeLocation.locationId,
                    $limit:1000,
                    $sort: {
                        createdAt: -1
                    }
                }
                if (state.employeeLocation.locationType!=="Front Desk"){
                    stuff.locationId=state.employeeLocation.locationId}  

            const findClient= await ClientServ.find(
                {query: stuff})

         await setFacilities(findClient.data)
                }
                else {
                    if (user.stacker){
                        const findClient= await ClientServ.find(
                            {query: {
                                $limit:100,
                                $sort: {
                                    createdAt: -1
                                }
                                }})

                    await setFacilities(findClient.data)

                    }
                }
            }


    useEffect(() => {

                if (user){
                    handleCalendarClose()
                }else{
                    /* const localUser= localStorage.getItem("user")
                    const user1=JSON.parse(localUser)
                    console.log(localUser)
                    console.log(user1)
                    fetchUser(user1)
                    console.log(user)
                    getFacilities(user) */
                }
                ClientServ.on('created', (obj)=>handleCalendarClose())
                ClientServ.on('updated', (obj)=>handleCalendarClose())
                ClientServ.on('patched', (obj)=>handleCalendarClose())
                ClientServ.on('removed', (obj)=>handleCalendarClose())
                const newClient={
                    selectedClient:{},
                    show :'create'
                    }
                 setState((prevstate)=>({...prevstate, ClientModule:newClient}))
                return () => {

                }
            },[])
const handleCalendarClose = async ()=>{
    let query= {
        start_time:{
            $gt:subDays(startDate,1),
            $lt:addDays(startDate,1)
        },
        facility:user.currentEmployee.facilityDetail._id,

        $limit:100,
        $sort: {
            createdAt: -1
        }
        }
        if (state.employeeLocation.locationType!=="Front Desk"){
            query.locationId=state.employeeLocation.locationId}  

    const findClient= await ClientServ.find(
        {query:query})

        await setFacilities(findClient.data)
}

const handleDate=async (date)=>{
    setStartDate(date)

}

    useEffect(() => {
        if (!!startDate){

            handleCalendarClose()
        }else{
            getFacilities()
        }

        return () => {

        }
    }, [startDate])
    //todo: pagination and vertical scroll bar

    return(
        <>
           {user?( <>  
                <div className="level">
                    <div className="level-left">
                        <div className="level-item">
                            <div className="field">
                                <p className="control has-icons-left  ">
                                    <DebounceInput className="input is-small " 
                                        type="text" placeholder="Search Appointments"
                                        minLength={3}
                                        debounceTimeout={400}
                                        onChange={(e)=>handleSearch(e.target.value)} />
                                    <span className="icon is-small is-left">
                                        <i className="fas fa-search"></i>
                                    </span>
                                </p>
                            </div>
                        </div>

                    <div className="level-item">
                        <DatePicker 
                            selected={startDate} 
                            onChange={date => handleDate(date)} 
                            dateFormat="dd/MM/yyyy"
                            placeholderText="Filter By Date"
                            isClearable
                            />
                        {/* <input name="filter_time"  ref={register ({ required: true })}  type="datetime-local" /> */}
                    </div>
                    </div>
                    <div className="level-item"> <span className="is-size-6 has-text-weight-medium">Appointments </span></div>
                    <div className="level-right">
                        <div className="level-item"> 
                            <div className="level-item"><div className="button is-success is-small" onClick={handleCreateNew}>New</div></div>
                        </div>
                    </div>

                </div>
                <div className="table-container pullup ">
                                <table className="table is-striped is-narrow is-hoverable is-fullwidth is-scrollable ">
                                    <thead>
                                        <tr>
                                        <th><abbr title="Serial No">S/No</abbr></th>
                                        <th><abbr title="Time">Date/Time</abbr></th>
                                        <th>First Name</th>
                                       <th><abbr title="Last Name">Last Name</abbr></th>
                                      {/*  <th><abbr title="Class">Classification</abbr></th> */}
                                        <th><abbr title="Location">Location</abbr></th> 
                                        {/* <th><abbr title="Phone">Phone</abbr></th>
                                         */}
                                        <th><abbr title="Type">Type</abbr></th>
                                        <th><abbr title="Status">Status</abbr></th>
                                        <th><abbr title="Reason">Procedure</abbr></th>
                                        <th><abbr title="Information">Information</abbr></th>
                                        <th><abbr title="Practitioner">Practitioner</abbr></th>
                                        {/* <th><abbr title="Actions">Actions</abbr></th> */}
                                        </tr>
                                    </thead>
                                    <tfoot>                                      
                                    </tfoot>
                                    <tbody>
                                        {facilities.map((Client, i)=>(

                                            <tr key={Client._id} onClick={()=>handleRow(Client)}  className={Client._id===(selectedAppointment?._id||null)?"is-selected":""}>
                                            <th>{i+1}</th>
                                            <td><strong>{format(new Date(Client.start_time),"dd-MM-yy HH:mm:ss")}</strong></td>
                                            <th>{Client.firstname}</th>
