        // to get the login information
        /* return await client.authenticate({
          strategy: 'local',
          email: 'hello@feathersjs.com',
          password: 'supersecret'
        }); */
        console.log(error)
      }
    })()
   }
}
 check().then((resp)=>{
  //console.log("testing")
 })
// eslint-disable-next-line 
