/* eslint-disable */
import React, {useState,useContext, useEffect,useRef} from 'react'
import {  useRouteMatch,  useHistory} from 'react-router-dom' //Route, Switch,Link, NavLink,
import client from '../../feathers'
import {DebounceInput} from 'react-debounce-input';
import { useForm } from "react-hook-form";
//import {useHistory} from 'react-router-dom'
import {UserContext,ObjectContext} from '../../context'
import {toast} from 'bulma-toast'
import { formatDistanceToNowStrict } from 'date-fns'
import ClientFinInfo from './ClientFinInfo';
import BillServiceCreate from '../Finance/BillServiceCreate'
import {AppointmentCreate} from '../Clinic/Appointments'
import InfiniteScroll from "react-infinite-scroll-component";
import ClientBilledPrescription from '../Finance/ClientBill'
import ClientGroup from './ClientGroup';
import DatePicker from "react-datepicker";

import "react-datepicker/dist/react-datepicker.css";
import {OrgFacilitySearch, SponsorSearch} from '../helpers/FacilitySearch'
var random = require('random-string-generator');
// eslint-disable-next-line
const searchfacility={};


export default function Beneficiary() {
    const {state}=useContext(ObjectContext) //,setState
    // eslint-disable-next-line
    const [selectedClient,setSelectedClient]=useState()
    //const [showState,setShowState]=useState() //create|modify|detail

    return(
        <section className= "section remPadTop">
            <div className="columns ">
            <div className="column is-6 ">
                <ClientList />
                </div>
            <div className="column is-6 ">
              {(state.ClientModule.show ==='List')&&<ClientList />}
                {(state.ClientModule.show ==='create')&&<BeneficiaryCreate />}
                {(state.ClientModule.show ==='detail')&&<ClientDetail  />}
                {(state.ClientModule.show ==='modify')&&<ClientModify Client={selectedClient} />} 
            </div>
            </div>                            
            </section>

    )

}

export function ClientCreate({closeModal}){
    const { register, handleSubmit,setValue,  getValues, reset} = useForm(); //, watch, errors, reset 
      // eslint-disable-next-line
    const [error, setError] =useState(false)
      // eslint-disable-next-line
    const [success, setSuccess] =useState(false)
      // eslint-disable-next-line
    const [message,setMessage] = useState("")
    // eslint-disable-next-line
    const [facility,setFacility] = useState()
    const ClientServ=client.service('client')
    const mpiServ=client.service('mpi')
    //const history = useHistory()
    const {user} = useContext(UserContext) //,setUser
    const [billModal, setBillModal] =useState(false)
    const [patList, setPatList] =useState([])
    const [dependant, setDependant] =useState(false)
    // eslint-disable-next-line
    const [currentUser,setCurrentUser] = useState()
    const [date,setDate] = useState()
    const {state,setState} = useContext(ObjectContext)


  // eslint-disable-next-line
    const getSearchfacility=(obj)=>{
        setValue("facility", obj._id,  {
            shouldValidate: true,
            shouldDirty: true
        })
    }

    const handleDate=async (date)=>{
        setDate(date)

    }
    useEffect(() => {
        setCurrentUser(user)
        //console.log(currentUser)
        return () => {

        }
    }, [user])

  //check user for facility or get list of facility  
    useEffect(()=>{
        //setFacility(user.activeClient.FacilityId)//
      if (!user.stacker){
       /*    console.log(currentUser)
        setValue("facility", user.currentEmployee.facilityDetail._id,  {
            shouldValidate: true,
            shouldDirty: true
        })  */
      }
    })

    const checkClient=()=>{
        const data= getValues()
        data.dob= date
       const obj ={
        firstname:data.firstname,
        middlename:data.middlename,
        lastname:data.lastname,
        gender:data.gender,
        email:data.email,
        phone:data.phone,
        dob:data.dob
       } 
       /* find if there is a match with the paramters entered
          run search if 
            1.phone no alone or  
            2.email alone or 
            3.both is entered
            4. all other 5 parameters

        */
       let query={}

       if (!!data.phone){
            query.phone=data.phone
            checkQuery(query)
       }

       if (!!data.email){
        query.email=data.email
        checkQuery(query)
       }

       if (!!data.firstname && !!data.lastname && !!data.gender && !!data.dob ){
          // console.log("simpa")
           data.middlename=data.middlename||""
           query.gender=data.gender,
           query.dob=data.dob,

           query.$or=[
                {
                    firstname: data.firstname,
                    lastname:data.lastname,
                    middlename: data.middlename
                },
                {
                    firstname: data.firstname,
                    lastname:data.middlename,
                    middlename: data.lastname
                },
                {
                    firstname: data.middlename,
                    lastname:data.lastname,
                    middlename: data.firstname
                },
                {
                    firstname: data.middlename,
                    lastname:data.firstname,
                    middlename: data.lastname
                },
                {
                    firstname: data.lastname,
                    lastname:data.firstname,
                    middlename: data.middlename
                },
                {
                    firstname: data.lastname,
                    lastname:data.middlename,
                    middlename: data.firstname
                },
        ]
        checkQuery(query)
       }


    }

    const checkQuery=(query)=>{
        setPatList([])
        if (!(query && Object.keys(query).length === 0 && query.constructor === Object)){
            ClientServ.find({query:query}).then((res)=>{
                console.log(res)
                if (res.total>0){
                   // alert(res.total)
                     setPatList(res.data)
                   setBillModal(true)
                   return
                }

            })
            .catch((err)=>{console.log(err)})
         }

    }


    const handleBill =()=>{
        setBillModal(true)
    }
    const  handlecloseModal3 = () =>{
        setBillModal(false)
        }

    const choosen=async (client)=>{
        //update client with facilities
      /*   if (client.facility !== user.currentEmployee.facilityDetail._id ){ //check taht it is not in list of related facilities


        //create mpi record
        const newPat = {
            client: client._id,
            facility:user.currentEmployee.facilityDetail._id,
            mrn:client.mrn,
            clientTags:client.clientTags,
            relfacilities:client.relatedfacilites
           }
           await mpiServ.create(newPat)
        } */
        //reset form
        //toast niotification
        //cash payment

    }
    const dupl=(client)=>{
        toast({
            message: 'Client previously registered in this facility',
            type: 'is-danger',
            dismissible: true,
            pauseOnHover: true,
          })
          reset()
          setPatList([])

    }
    const reg=async (client)=>{
        if ((client.relatedfacilities.findIndex(el=>el.facility===user.currentEmployee.facilityDetail._id))===-1){
            //create mpi record
            const newPat = {
                client: client._id,
                facility:user.currentEmployee.facilityDetail._id,
                mrn:client.mrn,
                clientTags:client.clientTags,
                relfacilities:client.relatedfacilities
               }
               //console.log(newPat)
               await mpiServ.create(newPat).then((resp)=>{
                toast({
                    message: 'Client created succesfully',
                    type: 'is-success',
                    dismissible: true,
                    pauseOnHover: true,
                  })
               }).catch((err)=>{
                toast({
                    message: 'Error creating Client '+err,
                    type: 'is-danger',
                    dismissible: true,
                    pauseOnHover: true,
                  })

               })
            }
            //reset form
            reset()
            setPatList([])
            //cash payment

    }
    const depen=(client)=>{
        setDependant(true)

    }
    const onSubmit = async (data,e) =>{
        if(!date){
            toast({
                message: 'Please enter Date of Birth! ' ,
                type: 'is-danger',
                dismissible: true,
                pauseOnHover: true,
              })

            return
        }
        e.preventDefault();
        setMessage("")
        setError(false)
        setSuccess(false)
        checkClient()
        if (patList.length>0){
             if(!dependant){
                return
             }
            //alert("something"+","+ patList.length)
            //let confirm = window.confirm("Is this person a dependant with parent phone number?")
           // setOption(confirm)
            setPatList([])

        }
         // data.createdby=user._id
        //  console.log(data);
          if (user.currentEmployee){
          data.facility=user.currentEmployee.facilityDetail._id  // or from facility dropdown
          }


        let confirm =window.confirm(`You are about to register a new client ${ data.firstname}  ${ data.middlename} ${ data.lastname} ?`)
        if (confirm){
            data.dob=date
        await ClientServ.create(data)
        .then((res)=>{
            console.log(res)
                //console.log(JSON.stringify(res))
                e.target.reset();
               /*  setMessage("Created Client successfully") */
                setSuccess(true)
                toast({
                    message: 'Client created succesfully',
                    type: 'is-success',
                    dismissible: true,
                    pauseOnHover: true,
                  })
                  setSuccess(false)
                  setPatList([])
                  setDependant(false)
                  setDate()
                  let newClientModule={}
                  //add to context
                        // if principal
                        if(state.currBeneficiary==="principal"){


                              newClientModule={
                                principal:res,
                                dependent:state.Beneficiary.dependent,
                                others:state.Beneficiary.others,
                                show:'create'
                                }       
                        }
                        if(state.currBeneficiary==="dependent"){

                             newClientModule={
                                principal:state.Beneficiary.principal,
